  1→package main
  2→
  3→import (
  4→	"context"
  5→	"fmt"
  6→	"sort"
  7→	"time"
  8→
  9→	"github.com/mark3labs/mcp-go/mcp"
 10→)
 11→
 12→var listTracksToolDef = mcp.NewTool("list_tracks",
 13→	mcp.WithDescription("Browse bGeigie Import tracks (bulk radiation measurement drives). Can filter by year and optionally month."),
 14→	mcp.WithNumber("year",
 15→		mcp.Description("Filter by year (e.g., 2024)"),
 16→		mcp.Min(2000), mcp.Max(2100),
 17→	),
 18→	mcp.WithNumber("month",
 19→		mcp.Description("Filter by month (1-12, requires year parameter)"),
 20→		mcp.Min(1), mcp.Max(12),
 21→	),
 22→	mcp.WithNumber("limit",
 23→		mcp.Description("Maximum number of results to return (default: 50, max: 50000)"),
 24→		mcp.Min(1), mcp.Max(50000),
 25→		mcp.DefaultNumber(50),
 26→	),
 27→	mcp.WithReadOnlyHintAnnotation(true),
 28→)
 29→
 30→func handleListTracks(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
 31→	year := req.GetInt("year", 0)
 32→	month := req.GetInt("month", 0)
 33→	limit := req.GetInt("limit", 50)
 34→
 35→	if month != 0 && year == 0 {
 36→		return mcp.NewToolResultError("Month filter requires year parameter"), nil
 37→	}
 38→	if year != 0 && (year < 2000 || year > 2100) {
 39→		return mcp.NewToolResultError("Year must be between 2000 and 2100"), nil
 40→	}
 41→	if month != 0 && (month < 1 || month > 12) {
 42→		return mcp.NewToolResultError("Month must be between 1 and 12"), nil
 43→	}
 44→	if limit < 1 || limit > 50000 {
 45→		return mcp.NewToolResultError("Limit must be between 1 and 50000"), nil
 46→	}
 47→
 48→	// Use API for latest data (no year) or recent years to ensure consistency with web UI
 49→	// and to avoid database replication lag. API also sorts by ID (upload order),
 50→	// which better matches "latest uploads" expectation than DB's recording_date sort.
 51→	currentYear := time.Now().Year()
 52→	if year == 0 || year >= currentYear-1 {
 53→		return listTracksAPI(ctx, year, month, limit)
 54→	}
 55→
 56→	if dbAvailable() {
 57→		return listTracksDB(ctx, year, month, limit)
 58→	}
 59→	return listTracksAPI(ctx, year, month, limit)
 60→}
 61→
 62→func listTracksDB(ctx context.Context, year, month, limit int) (*mcp.CallToolResult, error) {
 63→	query := `SELECT id, filename, file_type, track_id, file_size,
 64→			created_at, source, source_id, recording_date,
 65→			detector, username
 66→		FROM uploads
 67→		WHERE 1=1`
 68→
 69→	args := []any{}
 70→	argIdx := 1
 71→
 72→	if year != 0 {
 73→		startDate := time.Date(year, 1, 1, 0, 0, 0, 0, time.UTC)
 74→		endDate := time.Date(year+1, 1, 1, 0, 0, 0, 0, time.UTC)
 75→		if month != 0 {
 76→			startDate = time.Date(year, time.Month(month), 1, 0, 0, 0, 0, time.UTC)
 77→			if month == 12 {
 78→				endDate = time.Date(year+1, 1, 1, 0, 0, 0, 0, time.UTC)
 79→			} else {
 80→				endDate = time.Date(year, time.Month(month+1), 1, 0, 0, 0, 0, time.UTC)
 81→			}
 82→		}
 83→		query += fmt.Sprintf(" AND recording_date >= $%d AND recording_date < $%d", argIdx, argIdx+1)
 84→		args = append(args, startDate, endDate)
 85→		argIdx += 2
 86→	}
 87→
 88→	query += " ORDER BY recording_date DESC"
 89→	query += fmt.Sprintf(" LIMIT $%d", argIdx)
 90→	args = append(args, limit)
 91→
 92→	rows, err := queryRows(ctx, query, args...)
 93→	if err != nil {
 94→		return mcp.NewToolResultError(err.Error()), nil
 95→	}
 96→
 97→	// Get total count (with same filters)
 98→	countQuery := `SELECT count(*) AS total FROM uploads WHERE 1=1`
 99→	countArgs := []any{}
100→	if year != 0 {
101→		startDate := time.Date(year, 1, 1, 0, 0, 0, 0, time.UTC)
102→		endDate := time.Date(year+1, 1, 1, 0, 0, 0, 0, time.UTC)
103→		if month != 0 {
104→			startDate = time.Date(year, time.Month(month), 1, 0, 0, 0, 0, time.UTC)
105→			if month == 12 {
106→				endDate = time.Date(year+1, 1, 1, 0, 0, 0, 0, time.UTC)
107→			} else {
108→				endDate = time.Date(year, time.Month(month+1), 1, 0, 0, 0, 0, time.UTC)
109→			}
110→		}
111→		countQuery += " AND recording_date >= $1 AND recording_date < $2"
112→		countArgs = append(countArgs, startDate, endDate)
113→	}
114→	countRow, _ := queryRow(ctx, countQuery, countArgs...)
115→	total := 0
116→	if countRow != nil {
117→		if t, ok := countRow["total"]; ok {
118→			switch v := t.(type) {
119→			case int64:
120→				total = int(v)
121→			case float64:
122→				total = int(v)
123→			}
124→		}
125→	}
126→
127→	tracks := make([]map[string]any, len(rows))
128→	for i, r := range rows {
129→		tracks[i] = map[string]any{
130→			"id":             r["id"],
131→			"filename":       r["filename"],
132→			"track_id":       r["track_id"],
133→			"detector":       r["detector"],
134→			"username":       r["username"],
135→			"file_size":      r["file_size"],
136→			"recording_date": r["recording_date"],
137→			"created_at":     r["created_at"],
138→		}
139→	}
140→
141→	result := map[string]any{
142→		"count":           len(tracks),
143→		"total_available": total,
144→		"source":          "database",
145→		"filters": map[string]any{
146→			"year":  nilIfZero(year),
147→			"month": nilIfZero(month),
148→		},
149→		"tracks": tracks,
150→	}
151→
152→	return jsonResult(result)
153→}
154→
155→func listTracksAPI(ctx context.Context, year, month, limit int) (*mcp.CallToolResult, error) {
156→	var resp map[string]any
157→	var err error
158→
159→	if year != 0 && month != 0 {
160→		resp, err = client.GetTracksByMonth(ctx, year, month)
161→	} else if year != 0 {
162→		resp, err = client.GetTracksByYear(ctx, year)
163→	} else {
164→		resp, err = client.GetTracks(ctx)
165→	}
166→	if err != nil {
167→		return mcp.NewToolResultError(err.Error()), nil
168→	}
169→
170→	allTracks, _ := resp["tracks"].([]any)
171→	totalAvailable := len(allTracks)
172→
173→	// Sort by last_id descending so most recently uploaded tracks come first.
174→	sort.Slice(allTracks, func(i, j int) bool {
175→		return trackLastID(allTracks[i]) > trackLastID(allTracks[j])
176→	})
177→
178→	if limit > len(allTracks) {
179→		limit = len(allTracks)
180→	}
181→	limited := allTracks[:limit]
182→
183→	tracks := make([]map[string]any, 0, len(limited))
184→	for _, raw := range limited {
185→		t, ok := raw.(map[string]any)
186→		if !ok {
187→			continue
188→		}
189→		tracks = append(tracks, map[string]any{
190→			"track_id":     t["trackID"],
191→			"marker_count": t["markerCount"],
192→			"first_id":     t["firstID"],
193→			"last_id":      t["lastID"],
194→			"index":        t["index"],
195→			"api_url":      t["apiURL"],
196→		})
197→	}
198→
199→	result := map[string]any{
200→		"count":           len(tracks),
201→		"total_available": totalAvailable,
202→		"source":          "api",
203→		"filters": map[string]any{
204→			"year":  nilIfZero(year),
205→			"month": nilIfZero(month),
206→		},
207→		"tracks": tracks,
208→	}
209→
210→	return jsonResult(result)
211→}
212→
213→func nilIfZero(v int) any {
214→	if v == 0 {
215→		return nil
216→	}
217→	return v
218→}
219→
220→// trackLastID extracts the lastID field from a raw track map for sorting.
221→func trackLastID(v any) float64 {
222→	m, ok := v.(map[string]any)
223→	if !ok {
224→		return 0
225→	}
226→	f, ok := toFloat(m["lastID"])
227→	if !ok {
228→		return 0
229→	}
230→	return f
231→}
