  9→var dbInfoToolDef = mcp.NewTool("db_info",
 10→	mcp.WithDescription("Get database connection information and replication status (diagnostic tool)"),
 11→	mcp.WithReadOnlyHintAnnotation(true),
 12→)
 13→
 14→func handleDBInfo(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
 15→	if !dbAvailable() {
 16→		return mcp.NewToolResultText("No database connection (using REST API fallback)"), nil
 17→	}
 18→
 19→	// Get basic connection info
 20→	info := make(map[string]any)
 21→	
 22→	// Query PostgreSQL version
 23→	versionRow, err := queryRow(ctx, "SELECT version() AS version")
 24→	if err == nil && versionRow != nil {
 25→		info["version"] = versionRow["version"]
 26→	}
 27→
 28→	// Query current database name
 29→	dbNameRow, err := queryRow(ctx, "SELECT current_database() AS db_name")
 30→	if err == nil && dbNameRow != nil {
 31→		info["database"] = dbNameRow["db_name"]
 32→	}
 33→
 34→	// Query current user
 35→	userRow, err := queryRow(ctx, "SELECT current_user AS username")
 36→	if err == nil && userRow != nil {
 37→		info["user"] = userRow["username"]
 38→	}
 39→
 40→	// Query server address (useful for identifying which server we're connected to)
 41→	addrRow, err := queryRow(ctx, "SELECT inet_server_addr() AS server_addr, inet_server_port() AS server_port")
 42→	if err == nil && addrRow != nil {
 43→		info["server_address"] = addrRow["server_addr"]
 44→		info["server_port"] = addrRow["server_port"]
 45→	}
 46→
 47→	// Check if this is a replica (read-only mode)
 48→	isReplica := false
 49→	replicationRow, err := queryRow(ctx, "SELECT pg_is_in_recovery() AS in_recovery")
 50→	if err == nil && replicationRow != nil {
 51→		if val, ok := replicationRow["in_recovery"].(bool); ok {
 52→			isReplica = val
 53→		}
 54→		info["is_replica"] = isReplica
 55→		if isReplica {
 56→			info["mode"] = "read replica (replication lag possible)"
 57→		} else {
 58→			info["mode"] = "primary/master"
 59→		}
 60→	}
 61→
 62→	// If this is a replica, try to get replication lag
 63→	if isReplica {
 64→		lagRow, err := queryRow(ctx, `
 65→			SELECT 
 66→				EXTRACT(EPOCH FROM (now() - pg_last_xact_replay_timestamp())) AS lag_seconds,
 67→				pg_last_xact_replay_timestamp() AS last_replay_time
 68→		`)
 69→		if err == nil && lagRow != nil {
 70→			info["replication_lag_seconds"] = lagRow["lag_seconds"]
 71→			info["last_replay_time"] = lagRow["last_replay_time"]
 72→		}
 73→	}
 74→
 75→	// Get table counts for context
 76→	countsRow, err := queryRow(ctx, "SELECT count(*) AS total FROM uploads")
 77→	if err == nil && countsRow != nil {
 78→		if total, ok := countsRow["total"]; ok {
 79→			switch v := total.(type) {
 80→			case int64:
 81→				info["uploads_count"] = v
 82→			case float64:
 83→				info["uploads_count"] = int64(v)
 84→			}
 85→		}
 86→	}
 87→
 88→	// Get highest upload ID
 89→	maxRow, err := queryRow(ctx, "SELECT MAX(id) AS max_id FROM uploads")
 90→	if err == nil && maxRow != nil {
 91→		if maxID, ok := maxRow["max_id"]; ok {
 92→			info["max_upload_id"] = maxID
 93→		}
 94→	}
 95→
 96→	return jsonResult(map[string]any{
 97→		"status":     "connected",
 98→		"connection": info,
 99→	})
100→}
