106→// ============================================================
107→// MCP Client (Streamable HTTP transport)
108→// ============================================================
109→
110→type MCPClient struct {
111→	ServerURL  string
112→	HTTPClient *http.Client
113→	SessionID  string
114→	reqID      int
115→}
116→
117→func NewMCPClient(serverURL string) *MCPClient {
118→	return &MCPClient{
119→		ServerURL: serverURL,
120→		HTTPClient: &http.Client{
121→			Timeout: 30 * time.Second,
122→		},
123→	}
124→}
125→
126→func (c *MCPClient) nextID() int {
127→	c.reqID++
128→	return c.reqID
129→}
130→
131→// sendRequest sends a JSON-RPC request to the MCP server and returns the result.
132→// Handles both direct JSON responses and SSE-wrapped responses.
133→func (c *MCPClient) sendRequest(ctx context.Context, method string, params interface{}) (json.RawMessage, error) {
134→	rpcReq := MCPRequest{
135→		JSONRPC: "2.0",
136→		ID:      c.nextID(),
137→		Method:  method,
138→		Params:  params,
139→	}
140→
141→	body, err := json.Marshal(rpcReq)
142→	if err != nil {
143→		return nil, fmt.Errorf("marshal request: %w", err)
144→	}
145→
146→	req, err := http.NewRequestWithContext(ctx, "POST", c.ServerURL, bytes.NewReader(body))
147→	if err != nil {
148→		return nil, fmt.Errorf("create request: %w", err)
149→	}
150→	req.Header.Set("Content-Type", "application/json")
151→	req.Header.Set("Accept", "application/json, text/event-stream")
152→	if c.SessionID != "" {
153→		req.Header.Set("Mcp-Session-Id", c.SessionID)
154→	}
155→
156→	resp, err := c.HTTPClient.Do(req)
157→	if err != nil {
158→		return nil, fmt.Errorf("send request: %w", err)
159→	}
160→	defer resp.Body.Close()
161→
162→	// Capture session ID
163→	if sid := resp.Header.Get("Mcp-Session-Id"); sid != "" {
164→		c.SessionID = sid
165→	}
166→
167→	ct := resp.Header.Get("Content-Type")
168→
169→	// Handle SSE response
170→	if strings.Contains(ct, "text/event-stream") {
171→		return c.readSSEResponse(resp.Body, rpcReq.ID)
172→	}
173→
174→	// Handle direct JSON response
175→	var rpcResp MCPResponse
176→	if err := json.NewDecoder(resp.Body).Decode(&rpcResp); err != nil {
177→		return nil, fmt.Errorf("decode response: %w", err)
178→	}
179→	return rpcResp.Result, nil
180→}
181→
182→func (c *MCPClient) readSSEResponse(r io.Reader, expectedID int) (json.RawMessage, error) {
183→	scanner := bufio.NewScanner(r)
184→	var dataLines []string
185→
186→	for scanner.Scan() {
187→		line := scanner.Text()
188→
189→		if strings.HasPrefix(line, "data: ") {
190→			dataLines = append(dataLines, strings.TrimPrefix(line, "data: "))
191→		}
192→
193→		// Empty line = end of SSE event
194→		if line == "" && len(dataLines) > 0 {
195→			data := strings.Join(dataLines, "\n")
196→			dataLines = nil
197→
198→			var rpcResp MCPResponse
199→			if err := json.Unmarshal([]byte(data), &rpcResp); err != nil {
200→				continue // skip non-JSON events
201→			}
202→			if rpcResp.ID == expectedID {
203→				return rpcResp.Result, nil
204→			}
205→		}
206→	}
207→	return nil, fmt.Errorf("no matching response found in SSE stream")
208→}
209→
210→func (c *MCPClient) Initialize(ctx context.Context) error {
211→	params := map[string]interface{}{
212→		"protocolVersion": "2025-03-26",
213→		"capabilities":    map[string]interface{}{},
214→		"clientInfo": map[string]string{
215→			"name":    "go-mcp-qwen",
216→			"version": "1.0.0",
217→		},
218→	}
219→	_, err := c.sendRequest(ctx, "initialize", params)
220→	if err != nil {
221→		return fmt.Errorf("initialize: %w", err)
222→	}
223→
224→	// Send initialized notification (fire and forget)
225→	notif := MCPRequest{
226→		JSONRPC: "2.0",
227→		Method:  "notifications/initialized",
228→	}
229→	body, _ := json.Marshal(notif)
230→	req, _ := http.NewRequestWithContext(ctx, "POST", c.ServerURL, bytes.NewReader(body))
231→	req.Header.Set("Content-Type", "application/json")
232→	if c.SessionID != "" {
233→		req.Header.Set("Mcp-Session-Id", c.SessionID)
234→	}
235→	c.HTTPClient.Do(req)
236→
237→	return nil
238→}
239→
240→func (c *MCPClient) ListTools(ctx context.Context) ([]MCPTool, error) {
241→	result, err := c.sendRequest(ctx, "tools/list", nil)
242→	if err != nil {
243→		return nil, err
244→	}
245→	var toolsResult MCPToolsResult
246→	if err := json.Unmarshal(result, &toolsResult); err != nil {
247→		return nil, fmt.Errorf("unmarshal tools: %w", err)
248→	}
249→	return toolsResult.Tools, nil
250→}
251→
252→func (c *MCPClient) CallTool(ctx context.Context, name string, args map[string]interface{}) (string, error) {
253→	params := MCPCallToolParams{
254→		Name:      name,
255→		Arguments: args,
256→	}
257→	result, err := c.sendRequest(ctx, "tools/call", params)
258→	if err != nil {
259→		return "", err
260→	}
261→	var toolResult MCPToolResult
262→	if err := json.Unmarshal(result, &toolResult); err != nil {
263→		return "", fmt.Errorf("unmarshal tool result: %w", err)
264→	}
265→
266→	var texts []string
267→	for _, c := range toolResult.Content {
268→		if c.Type == "text" {
269→			texts = append(texts, c.Text)
270→		}
271→	}
272→	return strings.Join(texts, "\n"), nil
273→}
