275→// ============================================================
276→// Qwen Client
277→// ============================================================
278→
279→type QwenClient struct {
280→	APIKey     string
281→	BaseURL    string
282→	Model      string
283→	HTTPClient *http.Client
284→}
285→
286→func NewQwenClient(apiKey, model string) *QwenClient {
287→	return &QwenClient{
288→		APIKey:  apiKey,
289→		BaseURL: "https://dashscope.aliyuncs.com/compatible-mode/v1",
290→		Model:   model,
291→		HTTPClient: &http.Client{
292→			Timeout: 60 * time.Second,
293→		},
294→	}
295→}
296→
297→func (q *QwenClient) Chat(ctx context.Context, messages []Message, tools []Tool) (*Message, error) {
298→	chatReq := ChatRequest{
299→		Model:    q.Model,
300→		Messages: messages,
301→		Tools:    tools,
302→	}
303→	if len(tools) > 0 {
304→		chatReq.ToolChoice = "auto"
305→	}
306→
307→	body, err := json.Marshal(chatReq)
308→	if err != nil {
309→		return nil, err
310→	}
311→
312→	req, err := http.NewRequestWithContext(ctx, "POST", q.BaseURL+"/chat/completions", bytes.NewReader(body))
313→	if err != nil {
314→		return nil, err
315→	}
316→	req.Header.Set("Content-Type", "application/json")
317→	req.Header.Set("Authorization", "Bearer "+q.APIKey)
318→
319→	resp, err := q.HTTPClient.Do(req)
320→	if err != nil {
321→		return nil, err
322→	}
323→	defer resp.Body.Close()
324→
325→	if resp.StatusCode != 200 {
326→		b, _ := io.ReadAll(resp.Body)
327→		return nil, fmt.Errorf("qwen API error %d: %s", resp.StatusCode, string(b))
328→	}
329→
330→	var chatResp ChatResponse
331→	if err := json.NewDecoder(resp.Body).Decode(&chatResp); err != nil {
332→		return nil, err
333→	}
334→	if len(chatResp.Choices) == 0 {
335→		return nil, fmt.Errorf("no choices returned")
336→	}
337→	return &chatResp.Choices[0].Message, nil
338→}
