359→// ============================================================
360→// Agent: ties Qwen + MCP together with a tool-call loop
361→// ============================================================
362→
363→type Agent struct {
364→	qwen      *QwenClient
365→	mcp       *MCPClient
366→	tools     []Tool
367→	maxRounds int
368→}
369→
370→func NewAgent(qwen *QwenClient, mcp *MCPClient) *Agent {
371→	return &Agent{
372→		qwen:      qwen,
373→		mcp:       mcp,
374→		maxRounds: 5, // max tool-call round-trips
375→	}
376→}
377→
378→func (a *Agent) Init(ctx context.Context) error {
379→	if err := a.mcp.Initialize(ctx); err != nil {
380→		return fmt.Errorf("MCP init: %w", err)
381→	}
382→	mcpTools, err := a.mcp.ListTools(ctx)
383→	if err != nil {
384→		return fmt.Errorf("list tools: %w", err)
385→	}
386→	a.tools = mcpToolsToQwenTools(mcpTools)
387→	log.Printf("Loaded %d tools from MCP server", len(a.tools))
388→	for _, t := range a.tools {
389→		log.Printf("  - %s: %s", t.Function.Name, t.Function.Description)
390→	}
391→	return nil
392→}
393→
394→func (a *Agent) Run(ctx context.Context, userMessage string) (string, error) {
395→	messages := []Message{
396→		{Role: "system", Content: "You are a helpful assistant. Use the available tools to answer questions."},
397→		{Role: "user", Content: userMessage},
398→	}
399→
400→	for round := 0; round < a.maxRounds; round++ {
401→		resp, err := a.qwen.Chat(ctx, messages, a.tools)
402→		if err != nil {
403→			return "", fmt.Errorf("qwen chat round %d: %w", round, err)
404→		}
405→
406→		// No tool calls → we have the final answer
407→		if len(resp.ToolCalls) == 0 {
408→			return resp.Content, nil
409→		}
410→
411→		// Append assistant message with tool calls
412→		messages = append(messages, *resp)
413→
414→		// Execute each tool call against MCP
415→		for _, tc := range resp.ToolCalls {
416→			log.Printf("Tool call: %s(%s)", tc.Function.Name, tc.Function.Arguments)
417→
418→			var args map[string]interface{}
419→			if err := json.Unmarshal([]byte(tc.Function.Arguments), &args); err != nil {
420→				args = map[string]interface{}{}
421→			}
422→
423→			result, err := a.mcp.CallTool(ctx, tc.Function.Name, args)
424→			if err != nil {
425→				result = fmt.Sprintf("Error: %v", err)
426→			}
427→
428→			log.Printf("Tool result: %.200s...", result)
429→
430→			messages = append(messages, Message{
431→				Role:       "tool",
432→				ToolCallID: tc.ID,
433→				Content:    result,
434→			})
435→		}
436→	}
437→
438→	return "", fmt.Errorf("exceeded max tool-call rounds (%d)", a.maxRounds)
439→}
