458→	listenAddr := envOrDefault("LISTEN_ADDR", ":8080")
459→
460→	if qwenKey == "" {
461→		log.Fatal("ALIBABA_CLOUD_API_KEY or DASHSCOPE_API_KEY environment variable required")
462→	}
463→
464→	mcpClient := NewMCPClient(mcpURL)
465→	qwenClient := NewQwenClient(qwenKey, qwenModel)
466→	agent := NewAgent(qwenClient, mcpClient)
467→
468→	ctx := context.Background()
469→	if err := agent.Init(ctx); err != nil {
470→		log.Fatalf("Agent init failed: %v", err)
471→	}
472→
473→	// API endpoint for your website
474→	http.HandleFunc("/api/chat", func(w http.ResponseWriter, r *http.Request) {
475→		if r.Method != http.MethodPost {
476→			http.Error(w, "POST only", http.StatusMethodNotAllowed)
477→			return
478→		}
479→
480→		var req struct {
481→			Message string `json:"message"`
482→		}
483→		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
484→			http.Error(w, "bad request", http.StatusBadRequest)
485→			return
486→		}
487→
488→		answer, err := agent.Run(r.Context(), req.Message)
489→		if err != nil {
490→			log.Printf("Agent error: %v", err)
491→			http.Error(w, "internal error", http.StatusInternalServerError)
492→			return
493→		}
494→
495→		w.Header().Set("Content-Type", "application/json")
496→		json.NewEncoder(w).Encode(map[string]string{"response": answer})
497→	})
498→
499→	// Simple HTML page
500→	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
501→		w.Header().Set("Content-Type", "text/html")
502→		fmt.Fprint(w, indexHTML)
503→	})
504→
505→	log.Printf("Server starting on %s", listenAddr)
506→	log.Fatal(http.ListenAndServe(listenAddr, nil))
507→}
