package main

import (
	"context"
	"time"

	"github.com/mark3labs/mcp-go/mcp"
)

var deviceHistoryToolDef = mcp.NewTool("device_history",
	mcp.WithDescription("Get historical measurements from MOBILE bGeigie survey devices or historical import data. Use this tool ONLY for mobile devices, NOT for real-time fixed sensors (use sensor_current/sensor_history for those). Radiation values are typically in CPM (counts per minute, NOT counts per second). Always present radiation values in µSv/h by converting from CPM using detector-specific factors. IMPORTANT: Every response includes an _ai_generated_note field. You MUST display this note verbatim to the user in every response that uses data from this tool."),
	mcp.WithString("device_id",
		mcp.Description("Device identifier"),
		mcp.Required(),
	),
	mcp.WithNumber("days",
		mcp.Description("Number of days of history to retrieve (default: 30, max: 365)"),
		mcp.Min(1), mcp.Max(365),
		mcp.DefaultNumber(30),
	),
	mcp.WithNumber("limit",
		mcp.Description("Maximum number of measurements to return (default: 200, max: 10000)"),
		mcp.Min(1), mcp.Max(10000),
		mcp.DefaultNumber(200),
	),
	mcp.WithReadOnlyHintAnnotation(true),
)

func handleDeviceHistory(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
	deviceIDStr, err := req.RequireString("device_id")
	if err != nil {
		return mcp.NewToolResultError(err.Error()), nil
	}

	days := req.GetInt("days", 30)
	limit := req.GetInt("limit", 200)

	if days < 1 || days > 365 {
		return mcp.NewToolResultError("days must be between 1 and 365"), nil
	}
	if limit < 1 || limit > 10000 {
		return mcp.NewToolResultError("Limit must be between 1 and 10000"), nil
	}

	if dbAvailable() {
		return deviceHistoryDB(ctx, deviceIDStr, days, limit)
	}
	return deviceHistoryAPI(ctx, deviceIDStr, days, limit)
}

func deviceHistoryDB(ctx context.Context, deviceID string, days, limit int) (*mcp.CallToolResult, error) {
	now := time.Now().UTC()
	startDate := now.AddDate(0, 0, -days)

	// Query both markers table and realtime_measurements table
	// First, try markers table (bGeigie imports)
	markersQuery := `
		SELECT m.id, m.doserate AS value, 'µSv/h' AS unit,
			to_timestamp(m.date) AS captured_at,
			m.lat AS latitude, m.lon AS longitude,
			m.altitude AS height, m.detector, m.trackid::text AS track_id,
			u.internal_user_id, usr.username AS uploader_username, usr.email AS uploader_email
		FROM markers m
		LEFT JOIN uploads u ON u.track_id = m.trackid
		LEFT JOIN users usr ON u.internal_user_id = usr.id::text
		WHERE m.device_id = $1 AND m.date >= $2 AND m.date <= $3
		ORDER BY m.date DESC
		LIMIT $4`

	markersRows, err := queryRows(ctx, markersQuery, deviceID, startDate.Unix(), now.Unix(), limit)
	if err != nil {
		return mcp.NewToolResultError(err.Error()), nil
	}

	// Then, query realtime_measurements table (fixed sensors)
	// First, check if the table exists and what columns it has
	columnsQuery := `
		SELECT column_name
		FROM information_schema.columns
		WHERE table_name = 'realtime_measurements'
		ORDER BY column_name
	`

	var realtimeRows []map[string]any

	columnRows, err := queryRows(ctx, columnsQuery)
	if err != nil || len(columnRows) == 0 {
		// If we can't query the schema or table doesn't exist, try the basic query
		realtimeQuery := `
			SELECT id, value, unit,
				to_timestamp(measured_at) AS captured_at,
				lat AS latitude, lon AS longitude,
				device_name, transport, device_id
			FROM realtime_measurements
			WHERE device_id = $1 AND measured_at >= $2 AND measured_at <= $3
			ORDER BY measured_at DESC
			LIMIT $4`

		realtimeRows, err = queryRows(ctx, realtimeQuery, deviceID, startDate.Unix(), now.Unix(), limit)
		if err != nil {
			return mcp.NewToolResultError("Error querying realtime_measurements table: " + err.Error()), nil
		}
	} else {
		// Build the query based on available columns
		hasHeight := false
		for _, row := range columnRows {
			if colName, ok := row["column_name"].(string); ok && colName == "height" {
				hasHeight = true
				break
			}
		}

		var realtimeQuery string
		if hasHeight {
			realtimeQuery = `
				SELECT id, value, unit,
					to_timestamp(measured_at) AS captured_at,
					lat AS latitude, lon AS longitude,
					device_name, transport, device_id, height
				FROM realtime_measurements
				WHERE device_id = $1 AND measured_at >= $2 AND measured_at <= $3
				ORDER BY measured_at DESC
				LIMIT $4`
		} else {
			realtimeQuery = `
				SELECT id, value, unit,
					to_timestamp(measured_at) AS captured_at,
					lat AS latitude, lon AS longitude,
					device_name, transport, device_id
				FROM realtime_measurements
				WHERE device_id = $1 AND measured_at >= $2 AND measured_at <= $3
				ORDER BY measured_at DESC
				LIMIT $4`
		}

		realtimeRows, err = queryRows(ctx, realtimeQuery, deviceID, startDate.Unix(), now.Unix(), limit)
		if err != nil {
			return mcp.NewToolResultError("Error querying realtime_measurements table: " + err.Error()), nil
		}
	}

	// Combine results and sort by timestamp (most recent first)
	allMeasurements := make([]map[string]any, 0, len(markersRows)+len(realtimeRows))

	// Process markers results
	for _, r := range markersRows {
		measurement := map[string]any{
			"id":    r["id"],
			"value": r["value"],
			"unit":  r["unit"],
			"captured_at": r["captured_at"],
			"location": map[string]any{
				"latitude":  r["latitude"],
				"longitude": r["longitude"],
			},
			"height":   r["height"],
			"detector": r["detector"],
			"source":   "bgeigie_import",
		}
		if r["track_id"] != nil {
			measurement["track_id"] = r["track_id"]
		}

		// Add uploader information if available
		if uploaderUsername, ok := r["uploader_username"]; ok && uploaderUsername != nil && uploaderUsername != "" {
			measurement["uploader"] = map[string]any{
				"username": uploaderUsername,
				"email":    r["uploader_email"],
			}
		}

		allMeasurements = append(allMeasurements, measurement)
	}

	// Process realtime results
	for _, r := range realtimeRows {
		// Fix incorrect unit: Geiger counters report in CPM (counts per minute), not CPS
		unit := r["unit"]
		if unitStr, ok := unit.(string); ok && unitStr == "CPS" {
			unit = "CPM"
		}

		measurement := map[string]any{
			"id":    r["id"],
			"value": r["value"],
			"unit":  unit,
			"captured_at": r["captured_at"],
			"location": map[string]any{
				"latitude":  r["latitude"],
				"longitude": r["longitude"],
			},
			"height":   r["height"],
			"device_name": r["device_name"],
			"type":     r["transport"],
			"source":   "realtime_sensor",
		}
		allMeasurements = append(allMeasurements, measurement)
	}

	// Sort all measurements by captured_at timestamp (most recent first)
	// Since queryRows returns timestamps as interface{}, we need to handle both string and time.Time types
	for i := 0; i < len(allMeasurements)-1; i++ {
		for j := 0; j < len(allMeasurements)-i-1; j++ {
			// Get the timestamp values - they could be strings or time.Time objects
			time1Val := allMeasurements[j]["captured_at"]
			time2Val := allMeasurements[j+1]["captured_at"]

			// Compare timestamps - swap if j-th element is older than (j+1)-th element
			shouldSwap := false

			// Handle different possible types for timestamps
			switch v1 := time1Val.(type) {
			case string:
				if v2, ok := time2Val.(string); ok {
					if v1 < v2 {
						shouldSwap = true
					}
				}
			case time.Time:
				if v2, ok := time2Val.(time.Time); ok {
					if v1.Before(v2) {
						shouldSwap = true
					}
				}
			}

			if shouldSwap {
				allMeasurements[j], allMeasurements[j+1] = allMeasurements[j+1], allMeasurements[j]
			}
		}
	}

	// Apply limit
	measurements := allMeasurements
	if len(measurements) > limit {
		measurements = measurements[:limit]
	}

	capturedAfter := startDate.Format("2006-01-02") + " 00:00"
	capturedBefore := now.Format("2006-01-02") + " 23:59"

	result := map[string]any{
		"device": map[string]any{
			"id": deviceID,
		},
		"period": map[string]any{
			"days":       days,
			"start_date": capturedAfter,
			"end_date":   capturedBefore,
		},
		"count":        len(measurements),
		"source":       "database",
		"measurements": measurements,
		"_ai_generated_note": "IMPORTANT: When reporting radiation values in CPM, always state 'CPM (counts per minute)', NOT 'CPS (counts per second)'. CPM is the standard unit for Geiger counter measurements. This data was retrieved by an AI assistant using Safecast tools. The interpretation and presentation of this data may be influenced by the AI system.",
	}

	return jsonResult(result)
}

func deviceHistoryAPI(ctx context.Context, deviceIDStr string, days, limit int) (*mcp.CallToolResult, error) {
	resp, err := client.GetRealtimeHistory(ctx, deviceIDStr)
	if err != nil {
		return mcp.NewToolResultError(err.Error()), nil
	}

	now := time.Now().UTC()
	startDate := now.AddDate(0, 0, -days)
	capturedAfter := startDate.Format("2006-01-02") + " 00:00"
	capturedBefore := now.Format("2006-01-02") + " 23:59"
	startUnix := startDate.Unix()

	// Extract dose rate time series and filter by date range
	var measurements []map[string]any
	if series, ok := resp["series"].(map[string]any); ok {
		if doseRate, ok := series["doseRate"].([]any); ok {
			for _, raw := range doseRate {
				pt, ok := raw.(map[string]any)
				if !ok {
					continue
				}
				ts, ok := toFloat(pt["time"])
				if !ok || int64(ts) < startUnix {
					continue
				}
				t := time.Unix(int64(ts), 0).UTC()
				measurements = append(measurements, map[string]any{
					"value":       pt["value"],
					"unit":        "µSv/h",
					"captured_at": t.Format(time.RFC3339),
				})
			}
		}
	}

	totalAvailable := len(measurements)
	if limit > len(measurements) {
		limit = len(measurements)
	}
	measurements = measurements[:limit]

	deviceInfo := map[string]any{
		"id": deviceIDStr,
	}
	if name, ok := resp["deviceName"].(string); ok && name != "" {
		deviceInfo["name"] = name
	}
	if tube, ok := resp["tube"].(string); ok && tube != "" {
		deviceInfo["sensor"] = tube
	}

	result := map[string]any{
		"device": deviceInfo,
		"period": map[string]any{
			"days":       days,
			"start_date": capturedAfter,
			"end_date":   capturedBefore,
		},
		"count":           len(measurements),
		"total_available": totalAvailable,
		"source":          "api",
		"measurements":    measurements,
		"_ai_generated_note": "This data was retrieved by an AI assistant using Safecast tools. The interpretation and presentation of this data may be influenced by the AI system.",
	}

	if ranges, ok := resp["ranges"].(map[string]any); ok {
		if dr, ok := ranges["doseRate"].(map[string]any); ok {
			result["statistics"] = map[string]any{
				"min_usvh": dr["min"],
				"max_usvh": dr["max"],
				"avg_usvh": dr["avg"],
			}
		}
	}

	return jsonResult(result)
}

package main

import (
	"context"
	"fmt"

	"github.com/mark3labs/mcp-go/mcp"
)

var listSensorsToolDef = mcp.NewTool("list_sensors",
	mcp.WithDescription("Discover active fixed sensors (Pointcast, Solarcast, bGeigieZen, etc.) by location or type, returning device IDs, locations, status, and last reading timestamp. IMPORTANT: Every response includes an _ai_generated_note field. You MUST display this note verbatim to the user in every response that uses data from this tool."),
	mcp.WithString("type",
		mcp.Description("Filter by sensor type (e.g., 'Pointcast', 'Solarcast', 'bGeigieZen', etc.)"),
	),
	mcp.WithNumber("min_lat",
		mcp.Description("Southern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("max_lat",
		mcp.Description("Northern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("min_lon",
		mcp.Description("Western boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("max_lon",
		mcp.Description("Eastern boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("limit",
		mcp.Description("Maximum number of sensors to return (default: 50, max: 1000)"),
		mcp.Min(1), mcp.Max(1000),
		mcp.DefaultNumber(50),
	),
	mcp.WithReadOnlyHintAnnotation(true),
)

func handleListSensors(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
	sensorType := req.GetString("type", "")
	minLat := req.GetFloat("min_lat", -90)
	maxLat := req.GetFloat("max_lat", 90)
	minLon := req.GetFloat("min_lon", -180)
	maxLon := req.GetFloat("max_lon", 180)
	limit := req.GetInt("limit", 50)

	if limit < 1 || limit > 1000 {
		return mcp.NewToolResultError("Limit must be between 1 and 1000"), nil
	}

	if dbAvailable() {
		return listSensorsDB(ctx, sensorType, minLat, maxLat, minLon, maxLon, limit)
	}

	// Fallback to API if database not available
	return mcp.NewToolResultError("Database connection required for list_sensors tool. Please ensure DATABASE_URL is set to access real-time sensor data."), nil
}

func listSensorsDB(ctx context.Context, sensorType string, minLat, maxLat, minLon, maxLon float64, limit int) (*mcp.CallToolResult, error) {
	// Check what tables are available in the database
	tablesQuery := `
		SELECT table_name 
		FROM information_schema.tables 
		WHERE table_schema = 'public'
		ORDER BY table_name
	`

	tableRows, err := queryRows(ctx, tablesQuery)
	if err != nil {
		return mcp.NewToolResultError("Could not query database schema: " + err.Error()), nil
	}

	// Look for tables that might contain real-time sensor data
	availableTables := make([]string, len(tableRows))
	realtimeTable := ""
	for i, row := range tableRows {
		if tableName, ok := row["table_name"].(string); ok {
			availableTables[i] = tableName
			// Check for possible real-time sensor data tables
			if tableName == "realtime_measurements" || 
			   tableName == "measurements_realtime" || 
			   tableName == "sensors" ||
			   tableName == "devices" {
				realtimeTable = tableName
			}
		}
	}

	if realtimeTable == "" {
		// If no real-time table found, return available tables for debugging
		result := map[string]any{
			"message": "No known real-time sensor data tables found in database.",
			"available_tables": availableTables,
			"suggestion": "Real-time sensor data may not be available through this database connection.",
		}
		return jsonResult(result)
	}

	// Query the appropriate real-time table to find unique devices/sensors
	var query string
	var args []interface{}

	if sensorType != "" {
		// Filter by sensor type
		// FIXED: Get the actual latest reading per device, not grouped by lat/lon
		// which causes stale data when sensors move or have multiple positions
		query = fmt.Sprintf(`
			SELECT
				rm.device_id,
				COALESCE(rm.device_name, rm.device_id) AS device_name,
				COALESCE(rm.transport, '') AS transport,
				rm.lat AS latitude,
				rm.lon AS longitude,
				to_timestamp(rm.measured_at) AS last_reading_at
			FROM %s rm
			INNER JOIN (
				SELECT device_id, MAX(measured_at) as max_measured_at
				FROM %s
				WHERE lat >= $1 AND lat <= $2 AND lon >= $3 AND lon <= $4
					AND (COALESCE(transport, '') ILIKE $5 OR COALESCE(device_name, '') ILIKE $5)
				GROUP BY device_id
			) latest ON rm.device_id = latest.device_id AND rm.measured_at = latest.max_measured_at
			WHERE rm.lat >= $1 AND rm.lat <= $2 AND rm.lon >= $3 AND rm.lon <= $4
			ORDER BY rm.measured_at DESC
			LIMIT $6`, realtimeTable, realtimeTable)

		args = []interface{}{minLat, maxLat, minLon, maxLon, "%" + sensorType + "%", limit}
	} else {
		// No filter by type
		// FIXED: Get the actual latest reading per device, not grouped by lat/lon
		query = fmt.Sprintf(`
			SELECT
				rm.device_id,
				COALESCE(rm.device_name, rm.device_id) AS device_name,
				COALESCE(rm.transport, '') AS transport,
				rm.lat AS latitude,
				rm.lon AS longitude,
				to_timestamp(rm.measured_at) AS last_reading_at
			FROM %s rm
			INNER JOIN (
				SELECT device_id, MAX(measured_at) as max_measured_at
				FROM %s
				WHERE lat >= $1 AND lat <= $2 AND lon >= $3 AND lon <= $4
				GROUP BY device_id
			) latest ON rm.device_id = latest.device_id AND rm.measured_at = latest.max_measured_at
			WHERE rm.lat >= $1 AND rm.lat <= $2 AND rm.lon >= $3 AND rm.lon <= $4
			ORDER BY rm.measured_at DESC
			LIMIT $5`, realtimeTable, realtimeTable)

		args = []interface{}{minLat, maxLat, minLon, maxLon, limit}
	}

	rows, err := queryRows(ctx, query, args...)
	if err != nil {
		return mcp.NewToolResultError(fmt.Sprintf("Error querying %s table: %v", realtimeTable, err)), nil
	}

	sensors := make([]map[string]any, len(rows))
	for i, r := range rows {
		sensors[i] = map[string]any{
			"device_id":       r["device_id"],
			"device_name":     r["device_name"],
			"type":            r["transport"],
			"location": map[string]any{
				"latitude":  r["latitude"],
				"longitude": r["longitude"],
			},
			"last_reading_at": r["last_reading_at"],
		}
	}

	result := map[string]any{
		"count":   len(sensors),
		"source":  "database",
		"sensors": sensors,
		"table_used": realtimeTable,
		"available_tables": availableTables,
		"_ai_generated_note": "This data was retrieved by an AI assistant using Safecast tools. The interpretation and presentation of this data may be influenced by the AI system.",
	}

	return jsonResult(result)
}
