   299→                }
   300→                accumulated += ev.text;
   301→                botBubble.textContent = accumulated;
   302→                messagesEl.scrollTop = messagesEl.scrollHeight;
   303→              } else if (ev.type === 'done') {
   304→                finish();
   305→              } else if (ev.type === 'error') {
   306→                botBubble.classList.remove('thinking');
   307→                botBubble.classList.add('error');
   308→                botBubble.textContent = 'Error: ' + ev.error;
   309→                finish();
   310→              }
   311→            } catch { /* malformed line, skip */ }
   312→          }
   313→          return pump();
   314→        });
   315→      }
   316→      return pump();
   317→    }).catch(err => {
   318→      botBubble.classList.remove('thinking');
   319→      botBubble.classList.add('error');
   320→      botBubble.textContent = 'Connection error: ' + err.message;
