    68→
    69→type MCPRequest struct {
    70→	JSONRPC string      `json:"jsonrpc"`
    71→	ID      int         `json:"id"`
    72→	Method  string      `json:"method"`
    73→	Params  interface{} `json:"params,omitempty"`
    74→}
    75→
    76→type MCPResponse struct {
    77→	JSONRPC string          `json:"jsonrpc"`
