    90→}
    91→
    92→type MCPCallToolParams struct {
    93→	Name      string                 `json:"name"`
    94→	Arguments map[string]interface{} `json:"arguments"`
    95→}
    96→
    97→type MCPToolResult struct {
    98→	Content []MCPContent `json:"content"`
    99→}
   100→
   101→type MCPContent struct {
   102→	Type string `json:"type"`
   103→	Text string `json:"text"`
   104→}
   105→
   106→// ============================================================
   107→// MCP Client (Streamable HTTP transport)
   108→// ============================================================
   109→
   110→type MCPClient struct {
   111→	ServerURL  string
   112→	HTTPClient *http.Client
   113→	SessionID  string
   114→	reqID      int
   115→}
   116→
   117→func NewMCPClient(serverURL string) *MCPClient {
   118→	return &MCPClient{
   119→		ServerURL: serverURL,
   120→		HTTPClient: &http.Client{
   121→			Timeout: 30 * time.Second,
   122→		},
   123→	}
   124→}
   125→
   126→func (c *MCPClient) nextID() int {
   127→	c.reqID++
   128→	return c.reqID
   129→}
   130→
   131→// sendRequest sends a JSON-RPC request to the MCP server and returns the result.
   132→// Handles both direct JSON responses and SSE-wrapped responses.
   133→func (c *MCPClient) sendRequest(ctx context.Context, method string, params interface{}) (json.RawMessage, error) {
   134→	rpcReq := MCPRequest{
   135→		JSONRPC: "2.0",
   136→		ID:      c.nextID(),
   137→		Method:  method,
   138→		Params:  params,
   139→	}
   140→
   141→	body, err := json.Marshal(rpcReq)
   142→	if err != nil {
   143→		return nil, fmt.Errorf("marshal request: %w", err)
   144→	}
   145→
   146→	req, err := http.NewRequestWithContext(ctx, "POST", c.ServerURL, bytes.NewReader(body))
   147→	if err != nil {
   148→		return nil, fmt.Errorf("create request: %w", err)
   149→	}
   150→	req.Header.Set("Content-Type", "application/json")
   151→	req.Header.Set("Accept", "application/json, text/event-stream")
   152→	if c.SessionID != "" {
   153→		req.Header.Set("Mcp-Session-Id", c.SessionID)
   154→	}
   155→
   156→	resp, err := c.HTTPClient.Do(req)
   157→	if err != nil {
   158→		return nil, fmt.Errorf("send request: %w", err)
   159→	}
   160→	defer resp.Body.Close()
   161→
   162→	// Capture session ID
   163→	if sid := resp.Header.Get("Mcp-Session-Id"); sid != "" {
   164→		c.SessionID = sid
   165→	}
   166→
   167→	ct := resp.Header.Get("Content-Type")
   168→
   169→	// Handle SSE response
   170→	if strings.Contains(ct, "text/event-stream") {
   171→		return c.readSSEResponse(resp.Body, rpcReq.ID)
   172→	}
   173→
   174→	// Handle direct JSON response
   175→	var rpcResp MCPResponse
   176→	if err := json.NewDecoder(resp.Body).Decode(&rpcResp); err != nil {
   177→		return nil, fmt.Errorf("decode response: %w", err)
   178→	}
   179→	return rpcResp.Result, nil
   180→}
   181→
   182→func (c *MCPClient) readSSEResponse(r io.Reader, expectedID int) (json.RawMessage, error) {
   183→	scanner := bufio.NewScanner(r)
   184→	var dataLines []string
   185→
   186→	for scanner.Scan() {
   187→		line := scanner.Text()
   188→
   189→		if strings.HasPrefix(line, "data: ") {
   190→			dataLines = append(dataLines, strings.TrimPrefix(line, "data: "))
   191→		}
   192→
   193→		// Empty line = end of SSE event
   194→		if line == "" && len(dataLines) > 0 {
   195→			data := strings.Join(dataLines, "\n")
   196→			dataLines = nil
   197→
   198→			var rpcResp MCPResponse
   199→			if err := json.Unmarshal([]byte(data), &rpcResp); err != nil {
   200→				continue // skip non-JSON events
   201→			}
   202→			if rpcResp.ID == expectedID {
   203→				return rpcResp.Result, nil
   204→			}
   205→		}
   206→	}
   207→	return nil, fmt.Errorf("no matching response found in SSE stream")
   208→}
   209→
   210→func (c *MCPClient) Initialize(ctx context.Context) error {
   211→	params := map[string]interface{}{
   212→		"protocolVersion": "2025-03-26",
   213→		"capabilities":    map[string]interface{}{},
   214→		"clientInfo": map[string]string{
   215→			"name":    "go-mcp-qwen",
   216→			"version": "1.0.0",
   217→		},
   218→	}
   219→	_, err := c.sendRequest(ctx, "initialize", params)
   220→	if err != nil {
   221→		return fmt.Errorf("initialize: %w", err)
   222→	}
   223→
   224→	// Send initialized notification (fire and forget)
   225→	notif := MCPRequest{
   226→		JSONRPC: "2.0",
   227→		Method:  "notifications/initialized",
   228→	}
   229→	body, _ := json.Marshal(notif)
   230→	req, _ := http.NewRequestWithContext(ctx, "POST", c.ServerURL, bytes.NewReader(body))
   231→	req.Header.Set("Content-Type", "application/json")
   232→	if c.SessionID != "" {
   233→		req.Header.Set("Mcp-Session-Id", c.SessionID)
   234→	}
   235→	c.HTTPClient.Do(req)
   236→
   237→	return nil
   238→}
   239→
   240→func (c *MCPClient) ListTools(ctx context.Context) ([]MCPTool, error) {
   241→	result, err := c.sendRequest(ctx, "tools/list", nil)
   242→	if err != nil {
   243→		return nil, err
   244→	}
   245→	var toolsResult MCPToolsResult
   246→	if err := json.Unmarshal(result, &toolsResult); err != nil {
   247→		return nil, fmt.Errorf("unmarshal tools: %w", err)
   248→	}
   249→	return toolsResult.Tools, nil
   250→}
   251→
   252→func (c *MCPClient) CallTool(ctx context.Context, name string, args map[string]interface{}) (string, error) {
   253→	params := MCPCallToolParams{
   254→		Name:      name,
   255→		Arguments: args,
   256→	}
   257→	result, err := c.sendRequest(ctx, "tools/call", params)
   258→	if err != nil {
   259→		return "", err
   260→	}
   261→	var toolResult MCPToolResult
   262→	if err := json.Unmarshal(result, &toolResult); err != nil {
   263→		return "", fmt.Errorf("unmarshal tool result: %w", err)
   264→	}
   265→
   266→	var texts []string
   267→	for _, c := range toolResult.Content {
   268→		if c.Type == "text" {
   269→			texts = append(texts, c.Text)
   270→		}
   271→	}
   272→	return strings.Join(texts, "\n"), nil
   273→}
   274→
   275→// ============================================================
   276→// Qwen Client
   277→// ============================================================
   278→
   279→type QwenClient struct {
   280→	APIKey     string
   281→	BaseURL    string
   282→	Model      string
   283→	HTTPClient *http.Client
   284→}
   285→
   286→func NewQwenClient(apiKey, model string) *QwenClient {
   287→	return &QwenClient{
   288→		APIKey:  apiKey,
   289→		BaseURL: "https://dashscope.aliyuncs.com/compatible-mode/v1",
   290→		Model:   model,
   291→		HTTPClient: &http.Client{
   292→			Timeout: 60 * time.Second,
   293→		},
   294→	}
   295→}
   296→
   297→func (q *QwenClient) Chat(ctx context.Context, messages []Message, tools []Tool) (*Message, error) {
   298→	chatReq := ChatRequest{
   299→		Model:    q.Model,
   300→		Messages: messages,
   301→		Tools:    tools,
   302→	}
   303→	if len(tools) > 0 {
   304→		chatReq.ToolChoice = "auto"
   305→	}
   306→
   307→	body, err := json.Marshal(chatReq)
   308→	if err != nil {
   309→		return nil, err
   310→	}
   311→
   312→	req, err := http.NewRequestWithContext(ctx, "POST", q.BaseURL+"/chat/completions", bytes.NewReader(body))
   313→	if err != nil {
   314→		return nil, err
   315→	}
   316→	req.Header.Set("Content-Type", "application/json")
   317→	req.Header.Set("Authorization", "Bearer "+q.APIKey)
   318→
   319→	resp, err := q.HTTPClient.Do(req)
   320→	if err != nil {
   321→		return nil, err
   322→	}
   323→	defer resp.Body.Close()
   324→
   325→	if resp.StatusCode != 200 {
   326→		b, _ := io.ReadAll(resp.Body)
   327→		return nil, fmt.Errorf("qwen API error %d: %s", resp.StatusCode, string(b))
   328→	}
   329→
   330→	var chatResp ChatResponse
   331→	if err := json.NewDecoder(resp.Body).Decode(&chatResp); err != nil {
   332→		return nil, err
   333→	}
   334→	if len(chatResp.Choices) == 0 {
   335→		return nil, fmt.Errorf("no choices returned")
   336→	}
   337→	return &chatResp.Choices[0].Message, nil
   338→}
   339→
   340→// ============================================================
   341→// Bridge: convert MCP tools → Qwen tools
   342→// ============================================================
   343→
   344→func mcpToolsToQwenTools(mcpTools []MCPTool) []Tool {
   345→	tools := make([]Tool, len(mcpTools))
   346→	for i, t := range mcpTools {
   347→		tools[i] = Tool{
   348→			Type: "function",
   349→			Function: Function{
   350→				Name:        t.Name,
   351→				Description: t.Description,
   352→				Parameters:  t.InputSchema,
   353→			},
   354→		}
   355→	}
   356→	return tools
   357→}
   358→
   359→// ============================================================
   360→// Agent: ties Qwen + MCP together with a tool-call loop
   361→// ============================================================
   362→
   363→type Agent struct {
   364→	qwen      *QwenClient
   365→	mcp       *MCPClient
   366→	tools     []Tool
   367→	maxRounds int
   368→}
   369→
   370→func NewAgent(qwen *QwenClient, mcp *MCPClient) *Agent {
   371→	return &Agent{
   372→		qwen:      qwen,
   373→		mcp:       mcp,
   374→		maxRounds: 5, // max tool-call round-trips
   375→	}
   376→}
   377→
   378→func (a *Agent) Init(ctx context.Context) error {
   379→	if err := a.mcp.Initialize(ctx); err != nil {
   380→		return fmt.Errorf("MCP init: %w", err)
   381→	}
   382→	mcpTools, err := a.mcp.ListTools(ctx)
   383→	if err != nil {
   384→		return fmt.Errorf("list tools: %w", err)
   385→	}
   386→	a.tools = mcpToolsToQwenTools(mcpTools)
   387→	log.Printf("Loaded %d tools from MCP server", len(a.tools))
   388→	for _, t := range a.tools {
   389→		log.Printf("  - %s: %s", t.Function.Name, t.Function.Description)
   390→	}
   391→	return nil
   392→}
   393→
   394→func (a *Agent) Run(ctx context.Context, userMessage string) (string, error) {
   395→	systemPrompt := `You are a helpful assistant with access to Safecast radiation measurement tools. Follow these tool selection guidelines:
   396→
   397→REAL-TIME DATA TOOLS (use for live/current sensor data):
   398→- sensor_current: Get the latest reading from real-time fixed sensors (Pointcast, Solarcast, etc.)
   399→- sensor_history: Get time-series data from real-time fixed sensors over a date range
   400→
   401→HISTORICAL DATA TOOLS (use for mobile survey/bGeigie data):
   402→- device_history: Get historical measurements from mobile devices (bGeigie imports)
   403→- query_radiation: Search for measurements by location
   404→- search_area: Find measurements in a geographic bounding box
   405→- list_tracks, get_track: Browse mobile survey drives
   406→
   407→IMPORTANT: When the user asks about "real-time", "current", "latest", "live", or mentions fixed sensor types (Pointcast, Solarcast, bGeigieZen), you MUST use sensor_current or sensor_history, NOT device_history.
   408→
   409→Only use device_history for mobile bGeigie devices or when explicitly asked for historical survey data.
   410→
   411→UNIT CONVERSION REQUIREMENT:
   412→Always present radiation measurements in µSv/h (microsieverts per hour), not CPM (counts per minute).
   413→If data is provided in CPM, convert it using these detector-specific conversion factors:
   414→
   415→Common Geiger-Müller tube conversion factors (CPM to µSv/h):
   416→- LND 7317 (Pancake tube): µSv/h = CPM / 334
   417→- SBM-20 (Russian tube): µSv/h = CPM / 175.43
   418→- SBM-19: µSv/h = CPM / 108.3
   419→- J305 (bGeigie standard): µSv/h = CPM / 100
   420→- LND 78017: µSv/h = CPM / 294
   421→- SI-22G: µSv/h = CPM / 108
   422→- SI-3BG: µSv/h = CPM / 631
   423→
   424→If the detector type is known, use its specific conversion factor. If unknown, note that the value is in CPM and conversion requires knowing the detector model.`
   425→
   426→	messages := []Message{
   427→		{Role: "system", Content: systemPrompt},
   428→		{Role: "user", Content: userMessage},
   429→	}
   430→
   431→	for round := 0; round < a.maxRounds; round++ {
   432→		resp, err := a.qwen.Chat(ctx, messages, a.tools)
   433→		if err != nil {
   434→			return "", fmt.Errorf("qwen chat round %d: %w", round, err)
   435→		}
   436→
   437→		// No tool calls → we have the final answer
   438→		if len(resp.ToolCalls) == 0 {
   439→			return resp.Content, nil
   440→		}
   441→
   442→		// Append assistant message with tool calls
   443→		messages = append(messages, *resp)
   444→
   445→		// Execute each tool call against MCP
   446→		for _, tc := range resp.ToolCalls {
   447→			log.Printf("Tool call: %s(%s)", tc.Function.Name, tc.Function.Arguments)
   448→
   449→			var args map[string]interface{}
   450→			if err := json.Unmarshal([]byte(tc.Function.Arguments), &args); err != nil {
   451→				args = map[string]interface{}{}
   452→			}
   453→
   454→			result, err := a.mcp.CallTool(ctx, tc.Function.Name, args)
   455→			if err != nil {
   456→				result = fmt.Sprintf("Error: %v", err)
   457→			}
   458→
   459→			log.Printf("Tool result: %.200s...", result)
   460→
   461→			messages = append(messages, Message{
   462→				Role:       "tool",
   463→				ToolCallID: tc.ID,
   464→				Content:    result,
   465→			})
   466→		}
   467→	}
   468→
   469→	return "", fmt.Errorf("exceeded max tool-call rounds (%d)", a.maxRounds)
   470→}
   471→
   472→// ============================================================
   473→// HTTP handler for your website
   474→// ============================================================
   475→
   476→func main() {
   477→	// Test the API key first
   478→	testAPIKey()
   479→	
   480→	mcpURL := envOrDefault("MCP_SERVER_URL", "https://vps-01.safecast.jp/mcp-http")
   481→	
   482→	// Check for ALIBABA_CLOUD_API_KEY first, then fall back to DASHSCOPE_API_KEY
   483→	qwenKey := os.Getenv("ALIBABA_CLOUD_API_KEY")
   484→	if qwenKey == "" {
   485→		qwenKey = os.Getenv("DASHSCOPE_API_KEY")
   486→	}
   487→	
   488→	qwenModel := envOrDefault("QWEN_MODEL", "qwen-plus")
   489→	listenAddr := envOrDefault("LISTEN_ADDR", ":8080")
   490→
   491→	if qwenKey == "" {
   492→		log.Fatal("ALIBABA_CLOUD_API_KEY or DASHSCOPE_API_KEY environment variable required")
   493→	}
   494→
   495→	mcpClient := NewMCPClient(mcpURL)
   496→	qwenClient := NewQwenClient(qwenKey, qwenModel)
   497→	agent := NewAgent(qwenClient, mcpClient)
   498→
   499→	ctx := context.Background()
   500→	if err := agent.Init(ctx); err != nil {
   501→		log.Fatalf("Agent init failed: %v", err)
   502→	}
   503→
   504→	// API endpoint for your website
   505→	http.HandleFunc("/api/chat", func(w http.ResponseWriter, r *http.Request) {
   506→		if r.Method != http.MethodPost {
   507→			http.Error(w, "POST only", http.StatusMethodNotAllowed)
   508→			return
   509→		}
   510→
   511→		var req struct {
   512→			Message string `json:"message"`
   513→		}
   514→		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
   515→			http.Error(w, "bad request", http.StatusBadRequest)
   516→			return
   517→		}
   518→
   519→		answer, err := agent.Run(r.Context(), req.Message)
   520→		if err != nil {
   521→			log.Printf("Agent error: %v", err)
   522→			http.Error(w, "internal error", http.StatusInternalServerError)
   523→			return
   524→		}
   525→
   526→		w.Header().Set("Content-Type", "application/json")
   527→		json.NewEncoder(w).Encode(map[string]string{"response": answer})
   528→	})
   529→
   530→	// Simple HTML page
   531→	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
   532→		w.Header().Set("Content-Type", "text/html")
   533→		fmt.Fprint(w, indexHTML)
   534→	})
   535→
   536→	log.Printf("Server starting on %s", listenAddr)
   537→	log.Fatal(http.ListenAndServe(listenAddr, nil))
   538→}
   539→
   540→func envOrDefault(key, def string) string {
   541→	if v := os.Getenv(key); v != "" {
   542→		return v
   543→	}
   544→	return def
   545→}
   546→
   547→// Test API key function
   548→func testAPIKey() {
   549→	apiKey := os.Getenv("ALIBABA_CLOUD_API_KEY")
   550→	if apiKey == "" {
   551→		apiKey = os.Getenv("DASHSCOPE_API_KEY")
   552→	}
   553→	if apiKey == "" {
   554→		log.Fatal("ALIBABA_CLOUD_API_KEY or DASHSCOPE_API_KEY environment variable not set")
   555→	}
   556→
   557→	// Create a simple request to test the API key using the same endpoint as the Qwen client
   558→	client := &http.Client{Timeout: 30 * time.Second}
   559→
   560→	// Create a minimal chat completion request to test the API key
   561→	requestBody := `{"model":"qwen-turbo","messages":[{"role":"user","content":"test"}]}`
   562→	
   563→	req, err := http.NewRequest("POST", "https://dashscope.aliyuncs.com/compatible-mode/v1/chat/completions", strings.NewReader(requestBody))
   564→	if err != nil {
   565→		log.Printf("Error creating request: %v", err)
   566→		return
   567→	}
   568→
   569→	req.Header.Set("Authorization", "Bearer "+apiKey)
   570→	req.Header.Set("Content-Type", "application/json")
   571→
   572→	resp, err := client.Do(req)
   573→	if err != nil {
   574→		log.Printf("Error making request: %v", err)
   575→		return
   576→	}
   577→	defer resp.Body.Close()
   578→	
   579→	body, err := io.ReadAll(resp.Body)
   580→	if err != nil {
   581→		log.Printf("Error reading response: %v", err)
   582→		return
   583→	}
   584→	
   585→	if resp.StatusCode == 200 {
   586→		log.Println("✓ API key is valid! Successfully connected to Alibaba Cloud.")
   587→	} else {
   588→		log.Printf("✗ API key test failed. Status: %d, Response: %s", resp.StatusCode, string(body))
   589→	}
   590→}
   591→
   592→const indexHTML = `<!DOCTYPE html>
   593→<html>
   594→<head>
   595→  <title>MCP Chat</title>
   596→  <style>
   597→    body { font-family: sans-serif; max-width: 700px; margin: 40px auto; padding: 0 20px; }
   598→    #chat { border: 1px solid #ccc; padding: 16px; min-height: 300px; margin-bottom: 12px;
   599→            overflow-y: auto; max-height: 500px; white-space: pre-wrap; }
   600→    #input { width: calc(100% - 80px); padding: 8px; }
   601→    button { padding: 8px 16px; }
   602→    .user { color: #0066cc; }
   603→    .assistant { color: #333; }
   604→    .loading { color: #999; font-style: italic; }
   605→  </style>
   606→</head>
   607→<body>
   608→  <h2>Qwen + MCP Chat</h2>
   609→  <div id="chat"></div>
   610→  <input id="input" placeholder="Ask something..." onkeydown="if(event.key==='Enter')send()">
   611→  <button onclick="send()">Send</button>
   612→  <script>
   613→    const chat = document.getElementById('chat');
   614→    const input = document.getElementById('input');
   615→    async function send() {
   616→      const msg = input.value.trim();
   617→      if (!msg) return;
   618→      input.value = '';
   619→      chat.innerHTML += '<div class="user"><b>You:</b> ' + msg + '</div>';
   620→      chat.innerHTML += '<div class="loading">Thinking...</div>';
   621→      chat.scrollTop = chat.scrollHeight;
   622→      try {
   623→        const res = await fetch('/api/chat', {
