   124→
   125→    /* ── Input ── */
   126→    #input-area {
   127→      padding: 14px 20px 18px;
   128→      border-top: 1px solid var(--border);
   129→      flex-shrink: 0;
   130→    }
   131→    #form {
   132→      display: flex;
   133→      gap: 10px;
   134→      align-items: flex-end;
   135→    }
   136→    #msg {
   137→      flex: 1;
   138→      background: var(--surface);
   139→      border: 1px solid var(--border);
   140→      border-radius: 10px;
   141→      color: var(--text);
   142→      padding: 10px 14px;
   143→      font-size: 14px;
   144→      resize: none;
   145→      max-height: 120px;
   146→      outline: none;
   147→      font-family: inherit;
   148→      line-height: 1.5;
   149→      transition: border-color .15s;
   150→    }
   151→    #msg:focus { border-color: var(--green); }
   152→    #msg::placeholder { color: var(--muted); }
   153→
   154→    #send {
   155→      background: var(--green);
   156→      border: none;
   157→      border-radius: 10px;
   158→      width: 42px; height: 42px;
   159→      cursor: pointer;
   160→      display: flex; align-items: center; justify-content: center;
   161→      flex-shrink: 0;
   162→      transition: background .15s;
   163→    }
   164→    #send:hover:not(:disabled) { background: var(--green-dk); }
   165→    #send:disabled { opacity: .4; cursor: not-allowed; }
   166→    #send svg { fill: #fff; }
   167→  </style>
   168→</head>
   169→<body>
   170→<div id="app">
   171→  <header>
   172→    <div class="logo"><img src="safecast-square-ct.png" alt="Safecast" /></div>
   173→    <div>
   174→      <h1>Safecast Radiation Assistant</h1>
   175→      <p>Real-time radiation data from the Safecast sensor network</p>
   176→    </div>
   177→  </header>
   178→
   179→  <div id="messages">
   180→    <div id="intro">
   181→      <h2>Ask about radiation data</h2>
   182→      <p>I have access to live readings from Safecast sensors across Japan and worldwide.</p>
   183→      <div class="chips">
   184→        <span class="chip" onclick="ask(this)">What's the radiation level near Tokyo?</span>
   185→        <span class="chip" onclick="ask(this)">Show sensors near Fukushima</span>
   186→        <span class="chip" onclick="ask(this)">What are the latest readings in Osaka?</span>
   187→        <span class="chip" onclick="ask(this)">How does radiation compare year over year?</span>
   188→      </div>
   189→    </div>
   190→  </div>
   191→
   192→  <div id="input-area">
   193→    <form id="form" onsubmit="return false;">
   194→      <textarea id="msg" rows="1" placeholder="Ask about radiation levels, sensors, or locations…"></textarea>
   195→      <button id="send" type="submit" title="Send">
   196→        <svg width="18" height="18" viewBox="0 0 24 24"><path d="M2 21l21-9L2 3v7l15 2-15 2z"/></svg>
   197→      </button>
   198→    </form>
   199→  </div>
   200→</div>
   201→
   202→<script>
   203→  const messagesEl = document.getElementById('messages');
   204→  const introEl    = document.getElementById('intro');
   205→  const formEl     = document.getElementById('form');
   206→  const msgEl      = document.getElementById('msg');
   207→  const sendBtn    = document.getElementById('send');
   208→
   209→  let busy = false;
   210→
   211→  // Auto-grow textarea
   212→  msgEl.addEventListener('input', () => {
   213→    msgEl.style.height = 'auto';
   214→    msgEl.style.height = Math.min(msgEl.scrollHeight, 120) + 'px';
   215→  });
   216→
   217→  // Send on Enter (Shift+Enter = newline)
   218→  msgEl.addEventListener('keydown', e => {
   219→    if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); submit(); }
   220→  });
   221→
   222→  formEl.addEventListener('submit', submit);
   223→
   224→  function ask(chip) {
   225→    msgEl.value = chip.textContent;
   226→    submit();
   227→  }
   228→
   229→  function submit() {
   230→    const text = msgEl.value.trim();
   231→    if (!text || busy) return;
   232→    sendMessage(text);
   233→    msgEl.value = '';
   234→    msgEl.style.height = 'auto';
   235→  }
   236→
   237→  function addMessage(role, text, cls) {
   238→    if (introEl) introEl.style.display = 'none';
   239→
   240→    const msg    = document.createElement('div');
   241→    msg.className = `msg ${role}`;
   242→
   243→    const avatar = document.createElement('div');
   244→    avatar.className = 'avatar';
   245→    avatar.textContent = role === 'user' ? '👤' : '🤖';
   246→
   247→    const bubble = document.createElement('div');
   248→    bubble.className = `bubble ${cls || ''}`;
   249→    bubble.textContent = text;
   250→
   251→    msg.appendChild(avatar);
   252→    msg.appendChild(bubble);
   253→    messagesEl.appendChild(msg);
   254→    messagesEl.scrollTop = messagesEl.scrollHeight;
   255→    return bubble;
   256→  }
   257→
   258→  function sendMessage(text) {
   259→    busy = true;
   260→    sendBtn.disabled = true;
   261→
   262→    addMessage('user', text);
   263→    const botBubble = addMessage('bot', '…thinking…', 'thinking');
   264→
   265→    let accumulated = '';
   266→
   267→    function finish() {
   268→      botBubble.classList.remove('thinking');
   269→      busy = false;
   270→      sendBtn.disabled = false;
   271→      msgEl.focus();
   272→    }
   273→
   274→    fetch('/chat', {
   275→      method: 'POST',
   276→      headers: { 'Content-Type': 'application/json' },
   277→      body: JSON.stringify({ message: text }),
   278→    }).then(response => {
   279→      const reader  = response.body.getReader();
   280→      const decoder = new TextDecoder();
   281→      let   buffer  = '';
   282→
   283→      function pump() {
   284→        return reader.read().then(({ done, value }) => {
   285→          if (done) { finish(); return; }
   286→
   287→          buffer += decoder.decode(value, { stream: true });
   288→          const lines = buffer.split('\n');
   289→          buffer = lines.pop(); // keep incomplete last line
   290→
   291→          for (const line of lines) {
   292→            if (!line.trim()) continue;
   293→            try {
   294→              const ev = JSON.parse(line);
   295→              if (ev.type === 'text') {
   296→                if (botBubble.classList.contains('thinking')) {
   297→                  botBubble.classList.remove('thinking');
   298→                  accumulated = '';
   299→                }
   300→                accumulated += ev.text;
   301→                botBubble.textContent = accumulated;
   302→                messagesEl.scrollTop = messagesEl.scrollHeight;
   303→              } else if (ev.type === 'done') {
   304→                finish();
   305→              } else if (ev.type === 'error') {
   306→                botBubble.classList.remove('thinking');
   307→                botBubble.classList.add('error');
   308→                botBubble.textContent = 'Error: ' + ev.error;
   309→                finish();
   310→              }
   311→            } catch { /* malformed line, skip */ }
   312→          }
   313→          return pump();
   314→        });
   315→      }
   316→      return pump();
   317→    }).catch(err => {
   318→      botBubble.classList.remove('thinking');
   319→      botBubble.classList.add('error');
   320→      botBubble.textContent = 'Connection error: ' + err.message;
   321→      finish();
   322→    });
   323→  }
   324→
   325→  msgEl.focus();
   326→</script>
   327→</body>
   328→</html>
   329→

