   324→<script>
   325→  const messagesEl = document.getElementById('messages');
   326→  const introEl    = document.getElementById('intro');
   327→  const formEl     = document.getElementById('form');
   328→  const msgEl      = document.getElementById('msg');
   329→  const sendBtn    = document.getElementById('send');
   330→
   331→  let busy = false;
   332→  let conversationHistory = []; // Track conversation for context
   333→
   334→  // Auto-grow textarea
   335→  msgEl.addEventListener('input', () => {
   336→    msgEl.style.height = 'auto';
   337→    msgEl.style.height = Math.min(msgEl.scrollHeight, 120) + 'px';
