   349→    submit();
   350→  }
   351→
   352→  function submit() {
   353→    const text = msgEl.value.trim();
   354→    if (!text || busy) return;
   355→    sendMessage(text);
   356→    msgEl.value = '';
   357→    msgEl.style.height = 'auto';
   358→  }
   359→
   360→  // Convert markdown to HTML
   361→  function markdownToHTML(text) {
   362→    let html = text;
   363→
   364→    // Headers (must be at start of line)
   365→    html = html.replace(/^### (.+)$/gm, '<h3>$1</h3>');
   366→    html = html.replace(/^## (.+)$/gm, '<h2>$1</h2>');
   367→    html = html.replace(/^# (.+)$/gm, '<h1>$1</h1>');
   368→
   369→    // Horizontal rules
   370→    html = html.replace(/^---+$/gm, '<hr>');
   371→
   372→    // Tables (simple approach - detect table rows)
   373→    html = html.replace(/^\|(.+)\|$/gm, function(match, content) {
   374→      const cells = content.split('|').map(c => c.trim());
   375→      const cellTags = cells.map(c => {
   376→        // Check if this is a separator row (--|---|---)
   377→        if (/^:?-+:?$/.test(c)) return null;
   378→        return `<td>${c}</td>`;
   379→      }).filter(Boolean);
   380→      if (cellTags.length === 0) return '';
   381→      return '<tr>' + cellTags.join('') + '</tr>';
   382→    });
   383→    // Wrap table rows in table tags
   384→    html = html.replace(/(<tr>.+<\/tr>\n?)+/g, '<table>$&</table>');
   385→
   386→    // Unordered lists (lines starting with - or *)
   387→    html = html.replace(/^[*-] (.+)$/gm, '<li>$1</li>');
   388→    html = html.replace(/(<li>.+<\/li>\n?)+/g, '<ul>$&</ul>');
   389→
   390→    // Ordered lists (lines starting with 1., 2., etc)
   391→    html = html.replace(/^\d+\. (.+)$/gm, '<li>$1</li>');
   392→
   393→    // Bold: **text** or __text__
   394→    html = html.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
   395→    html = html.replace(/__(.+?)__/g, '<strong>$1</strong>');
   396→
   397→    // Italic: *text* or _text_
   398→    html = html.replace(/\*(.+?)\*/g, '<em>$1</em>');
   399→    html = html.replace(/_(.+?)_/g, '<em>$1</em>');
   400→
   401→    // Inline code: `code`
   402→    html = html.replace(/`(.+?)`/g, '<code>$1</code>');
   403→
   404→    // Links: [text](url)
   405→    html = html.replace(/\[(.+?)\]\((.+?)\)/g, '<a href="$2" target="_blank" rel="noopener">$1</a>');
   406→
   407→    // Line breaks: preserve newlines, but not after block elements or between table rows
   408→    html = html.replace(/\n/g, (match, offset, string) => {
   409→      // Don't add <br> after closing block tags
   410→      const beforeContext = string.substring(Math.max(0, offset - 50), offset);
   411→      if (/<\/(table|ul|h[123]|tr)>\s*$/.test(beforeContext)) return '';
   412→      // Don't add <br> before opening table rows
   413→      const afterContext = string.substring(offset + 1, Math.min(string.length, offset + 10));
   414→      if (/^\s*<tr>/.test(afterContext)) return '';
   415→      return '<br>';
   416→    });
   417→
   418→    return html;
   419→  }
   420→
   421→  function addMessage(role, text, cls) {
   422→    if (introEl) introEl.style.display = 'none';
   423→
   424→    const msg    = document.createElement('div');
   425→    msg.className = `msg ${role}`;
   426→
