   436→    }
   437→
   438→    const bubble = document.createElement('div');
   439→    bubble.className = `bubble ${cls || ''}`;
   440→    // User messages are plain text, bot messages support markdown
   441→    if (role === 'bot') {
   442→      bubble.innerHTML = markdownToHTML(text);
   443→
   444→      // Add copy button for bot messages
   445→      const copyBtn = document.createElement('button');
   446→      copyBtn.className = 'copy-btn';
   447→      copyBtn.textContent = 'Copy';
   448→      copyBtn.onclick = () => copyMessage(text, copyBtn);
   449→      bubble.appendChild(copyBtn);
   450→    } else {
   451→      bubble.textContent = text;
   452→    }
   453→
   454→    msg.appendChild(avatar);
   455→    msg.appendChild(bubble);
   456→    messagesEl.appendChild(msg);
   457→    messagesEl.scrollTop = messagesEl.scrollHeight;
   458→    return bubble;
   459→  }
   460→
   461→  function copyMessage(text, btn) {
   462→    navigator.clipboard.writeText(text).then(() => {
   463→      const originalText = btn.textContent;
   464→      btn.textContent = 'Copied!';
   465→      btn.classList.add('copied');
   466→      setTimeout(() => {
   467→        btn.textContent = originalText;
   468→        btn.classList.remove('copied');
   469→      }, 2000);
   470→    }).catch(err => {
   471→      console.error('Failed to copy:', err);
   472→    });
   473→  }
   474→
   475→  function downloadConversation() {
   476→    if (conversationHistory.length === 0) {
   477→      alert('No conversation to download yet!');
   478→      return;
   479→    }
   480→
   481→    // Build markdown format
   482→    let markdown = '# Safecast Radiation Assistant Conversation\n\n';
   483→    markdown += `Date: ${new Date().toLocaleString()}\n\n`;
   484→    markdown += '---\n\n';
   485→
   486→    conversationHistory.forEach(msg => {
   487→      const role = msg.role === 'user' ? 'You' : 'Assistant';
   488→      markdown += `## ${role}\n\n`;
   489→      markdown += msg.content + '\n\n';
   490→      markdown += '---\n\n';
   491→    });
   492→
   493→    // Create download link
   494→    const blob = new Blob([markdown], { type: 'text/markdown' });
   495→    const url = URL.createObjectURL(blob);
   496→    const a = document.createElement('a');
   497→    a.href = url;
   498→    a.download = `safecast-conversation-${new Date().toISOString().slice(0,10)}.md`;
   499→    document.body.appendChild(a);
   500→    a.click();
   501→    document.body.removeChild(a);
   502→    URL.revokeObjectURL(url);
   503→  }
   504→
   505→  function clearConversation() {
   506→    // Clear conversation history
   507→    conversationHistory = [];
   508→
   509→    // Clear all messages
   510→    while (messagesEl.firstChild) {
   511→      messagesEl.removeChild(messagesEl.firstChild);
   512→    }
   513→
   514→    // Show intro again
   515→    messagesEl.appendChild(introEl);
   516→    introEl.style.display = 'block';
   517→
   518→    // Focus on input
   519→    msgEl.focus();
   520→  }
   521→
   522→  function sendMessage(text) {
   523→    busy = true;
   524→    sendBtn.disabled = true;
   525→
   526→    addMessage('user', text);
   527→    const botBubble = addMessage('bot', '…thinking…', 'thinking');
   528→
   529→    let accumulated = '';
   530→
   531→    function finish(success = true) {
   532→      botBubble.classList.remove('thinking');
   533→      busy = false;
   534→      sendBtn.disabled = false;
   535→      msgEl.focus();
   536→
   537→      // Add AI disclaimer to successful bot messages
   538→      if (success && accumulated) {
   539→        // Re-render with disclaimer
   540→        const disclaimerText = '\n\n---\n\n_Note: AI-generated response using data from the Safecast radiation monitoring network. For critical safety decisions, please consult official sources._';
   541→        botBubble.innerHTML = markdownToHTML(accumulated + disclaimerText);
   542→
   543→        // Re-add copy button (since we replaced innerHTML)
   544→        const copyBtn = document.createElement('button');
   545→        copyBtn.className = 'copy-btn';
   546→        copyBtn.textContent = 'Copy';
   547→        copyBtn.onclick = () => copyMessage(accumulated, copyBtn); // Copy without disclaimer
   548→        botBubble.appendChild(copyBtn);
   549→
   550→        conversationHistory.push(
   551→          { role: 'user', content: text },
   552→          { role: 'assistant', content: accumulated }
   553→        );
   554→
   555→        // Keep only last 10 messages (5 exchanges) to prevent rate limits
   556→        const MAX_HISTORY = 10;
   557→        if (conversationHistory.length > MAX_HISTORY) {
   558→          conversationHistory = conversationHistory.slice(-MAX_HISTORY);
   559→        }
   560→      }
   561→    }
   562→
   563→    fetch('/chat', {
   564→      method: 'POST',
   565→      headers: { 'Content-Type': 'application/json' },
   566→      body: JSON.stringify({
   567→        message: text,
   568→        history: conversationHistory
   569→      }),
   570→    }).then(response => {
   571→      const reader  = response.body.getReader();
   572→      const decoder = new TextDecoder();
   573→      let   buffer  = '';
   574→
   575→      function pump() {
   576→        return reader.read().then(({ done, value }) => {
   577→          if (done) { finish(); return; }
   578→
   579→          buffer += decoder.decode(value, { stream: true });
   580→          const lines = buffer.split('\n');
   581→          buffer = lines.pop(); // keep incomplete last line
   582→
   583→          for (const line of lines) {
   584→            if (!line.trim()) continue;
   585→            try {
   586→              const ev = JSON.parse(line);
   587→              if (ev.type === 'text') {
   588→                if (botBubble.classList.contains('thinking')) {
   589→                  botBubble.classList.remove('thinking');
   590→                  accumulated = '';
   591→                }
   592→                accumulated += ev.text;
   593→                botBubble.innerHTML = markdownToHTML(accumulated);
   594→                messagesEl.scrollTop = messagesEl.scrollHeight;
   595→              } else if (ev.type === 'done') {
   596→                finish();
   597→              } else if (ev.type === 'error') {
   598→                botBubble.classList.remove('thinking');
   599→                botBubble.classList.add('error');
   600→                botBubble.textContent = 'Error: ' + ev.error;
   601→                finish(false);
   602→              }
   603→            } catch { /* malformed line, skip */ }
   604→          }
   605→          return pump();
   606→        });
   607→      }
   608→      return pump();
   609→    }).catch(err => {
   610→      botBubble.classList.remove('thinking');
   611→      botBubble.classList.add('error');
   612→      botBubble.textContent = 'Connection error: ' + err.message;
   613→      finish(false);
   614→    });
   615→  }
   616→
   617→  msgEl.focus();
