     1<!DOCTYPE html>
     2<html lang="en">
     3<head>
     4  <meta charset="UTF-8" />
     5  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
     6  <title>Safecast Radiation Assistant</title>
     7  <link rel="icon" type="image/png" href="safecast-square-ct.png" />
     8  <style>
     9    *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
    10
    11    :root {
    12      --bg:       #1a1e24;
    13      --surface:  #242930;
    14      --border:   #2e3540;
    15      --blue:     #1e88e5;
    16      --blue-dk:  #1565c0;
    17      --green:    #1e88e5;
    18      --green-dk: #1565c0;
    19      --text:     #e4e8ef;
    20      --muted:    #7a8494;
    21      --user-bg:  #1e88e522;
    22      --err:      #e17055;
    23    }
    24
    25    html, body {
    26      height: 100%;
    27      background: var(--bg);
    28      color: var(--text);
    29      font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
    30      font-size: 15px;
    31      line-height: 1.6;
    32    }
    33
    34    /*  Layout  */
    35    #app {
    36      display: flex;
    37      flex-direction: column;
    38      height: 100%;
    39      max-width: 820px;
    40      margin: 0 auto;
    41    }
    42
    43    header {
    44      padding: 16px 20px 14px;
    45      border-bottom: 1px solid var(--border);
    46      display: flex;
    47      align-items: center;
    48      gap: 10px;
    49      flex-shrink: 0;
    50    }
    51    header .logo {
    52      width: 32px; height: 32px;
    53      border-radius: 8px;
    54      display: flex; align-items: center; justify-content: center;
    55      overflow: hidden;
    56    }
    57    header .logo img {
    58      width: 100%; height: 100%;
    59      object-fit: contain;
    60    }
    61    header .title-area { flex: 1; }
    62    header h1 { font-size: 16px; font-weight: 600; }
    63    header p  { font-size: 12px; color: var(--muted); }
    64
    65    /* Download button in header */
    66    #download-btn {
    67      background: var(--surface);
    68      border: 1px solid var(--border);
    69      border-radius: 8px;
    70      padding: 6px 12px;
    71      font-size: 13px;
    72      cursor: pointer;
    73      color: var(--text);
    74      display: flex;
    75      align-items: center;
    76      gap: 6px;
    77      transition: border-color .15s, color .15s;
    78    }
    79    #download-btn:hover { border-color: var(--green); color: var(--green); }
    80    #download-btn svg { width: 14px; height: 14px; fill: currentColor; }
    81
    82    /*  Messages  */
    83    #messages {
    84      flex: 1;
    85      overflow-y: auto;
    86      padding: 20px;
    87      display: flex;
    88      flex-direction: column;
    89      gap: 16px;
    90    }
    91
    92    .msg {
    93      display: flex;
    94      gap: 10px;
    95      max-width: 100%;
    96      position: relative;
    97    }
    98    .msg.user  { flex-direction: row-reverse; }
    99    .msg.user  .bubble { background: var(--user-bg); border: 1px solid var(--green); margin-left: 40px; }
   100    .msg.bot   .bubble { background: var(--surface); border: 1px solid var(--border); margin-right: 40px; position: relative; }
   101
   102    /* Copy button for bot messages */
   103    .msg.bot .copy-btn {
   104      position: absolute;
   105      top: 8px;
   106      right: 8px;
   107      background: var(--surface);
   108      border: 1px solid var(--border);
   109      border-radius: 6px;
   110      padding: 4px 8px;
   111      font-size: 11px;
   112      cursor: pointer;
   113      color: var(--muted);
   114      opacity: 0;
   115      transition: opacity .15s, color .15s, border-color .15s;
   116    }
   117    .msg.bot:hover .copy-btn { opacity: 1; }
   118    .msg.bot .copy-btn:hover { color: var(--green); border-color: var(--green); }
   119    .msg.bot .copy-btn.copied { color: var(--green); opacity: 1; }
   120
   121    .avatar {
   122      width: 30px; height: 30px; border-radius: 50%;
   123      flex-shrink: 0; display: flex; align-items: center; justify-content: center;
   124      font-size: 14px;
   125    }
   126    .msg.user .avatar { background: var(--green); color: #fff; }
   127    .msg.bot  .avatar { background: var(--surface); border: 1px solid var(--border); }
   128
   129    .bubble {
   130      padding: 10px 14px;
   131      border-radius: 12px;
   132      white-space: pre-wrap;
   133      word-break: break-word;
   134      line-height: 1.55;
   135    }
   136    .bubble strong { font-weight: 600; }
   137    .bubble em { font-style: italic; }
   138    .bubble code {
   139      background: rgba(255, 255, 255, 0.1);
   140      padding: 2px 6px;
   141      border-radius: 4px;
   142      font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
   143      font-size: 13px;
   144    }
   145    .bubble a {
   146      color: var(--blue);
   147      text-decoration: none;
   148      border-bottom: 1px solid transparent;
   149      transition: border-color .15s;
   150    }
   151    .bubble a:hover { border-bottom-color: var(--blue); }
   152
   153    /* Markdown headers */
   154    .bubble h1 { font-size: 20px; font-weight: 700; margin: 16px 0 10px; color: var(--text); }
   155    .bubble h2 { font-size: 18px; font-weight: 600; margin: 14px 0 8px; color: var(--text); }
   156    .bubble h3 { font-size: 16px; font-weight: 600; margin: 12px 0 6px; color: var(--text); }
   157    .bubble h1 + br, .bubble h2 + br, .bubble h3 + br { display: none; } /* Remove line breaks after headers */
   158
   159    /* Markdown tables */
   160    .bubble table {
   161      width: 100%;
   162      border-collapse: collapse;
   163      margin: 12px 0;
   164      background: rgba(255, 255, 255, 0.03);
   165      border-radius: 6px;
   166      overflow: hidden;
   167      line-height: 0; /* Collapse spacing between rows */
   168    }
   169    .bubble table td {
   170      padding: 8px 12px;
   171      border: 1px solid var(--border);
   172      line-height: 1.4; /* Restore line height inside cells */
   173    }
   174    .bubble table tr:first-child td {
   175      background: rgba(255, 255, 255, 0.08);
   176      font-weight: 600;
   177    }
   178    .bubble table br { display: none; } /* Remove line breaks inside tables */
   179
   180    /* Markdown lists */
   181    .bubble ul {
   182      margin: 10px 0;
   183      padding-left: 24px;
   184      list-style-type: disc;
   185    }
   186    .bubble ul li { margin: 4px 0; }
   187    .bubble ul br { display: none; } /* Remove line breaks inside lists */
   188
   189    /* Markdown horizontal rule */
   190    .bubble hr {
   191      border: none;
   192      border-top: 1px solid var(--border);
   193      margin: 16px 0;
   194    }
   195
   196    .bubble.thinking {
   197      color: var(--muted);
   198      font-style: italic;
   199      animation: pulse 1.2s ease-in-out infinite;
   200    }
   201    @keyframes pulse { 0%,100% { opacity: 1; } 50% { opacity: .4; } }
   202
   203    .bubble.error { color: var(--err); border-color: var(--err); }
   204
   205    /*  Intro  */
   206    #intro {
   207      text-align: center;
   208      padding: 40px 20px;
   209      color: var(--muted);
   210    }
   211    #intro h2 { font-size: 22px; color: var(--text); margin-bottom: 8px; }
   212    #intro p  { font-size: 14px; max-width: 400px; margin: 0 auto 20px; }
   213    #intro .chips { display: flex; flex-wrap: wrap; gap: 8px; justify-content: center; }
   214    #intro .chip {
   215      background: var(--surface);
   216      border: 1px solid var(--border);
   217      border-radius: 20px;
   218      padding: 6px 14px;
   219      font-size: 13px;
   220      cursor: pointer;
   221      transition: border-color .15s;
   222    }
   223    #intro .chip:hover { border-color: var(--green); color: var(--green); }
   224
   225    /*  Input  */
   226    #input-area {
   227      padding: 14px 20px 18px;
   228      border-top: 1px solid var(--border);
   229      flex-shrink: 0;
   230    }
   231    #form {
   232      display: flex;
   233      gap: 10px;
   234      align-items: flex-end;
   235    }
   236    #msg {
   237      flex: 1;
   238      background: var(--surface);
   239      border: 1px solid var(--border);
   240      border-radius: 10px;
   241      color: var(--text);
   242      padding: 10px 14px;
   243      font-size: 14px;
   244      resize: none;
   245      max-height: 120px;
   246      outline: none;
   247      font-family: inherit;
   248      line-height: 1.5;
   249      transition: border-color .15s;
   250    }
   251    #msg:focus { border-color: var(--green); }
   252    #msg::placeholder { color: var(--muted); }
   253
   254    #send {
   255      background: var(--green);
   256      border: none;
   257      border-radius: 10px;
   258      width: 42px; height: 42px;
   259      cursor: pointer;
   260      display: flex; align-items: center; justify-content: center;
   261      flex-shrink: 0;
   262      transition: background .15s;
   263    }
   264    #send:hover:not(:disabled) { background: var(--green-dk); }
   265    #send:disabled { opacity: .4; cursor: not-allowed; }
   266    #send svg { fill: #fff; }
   267  </style>
   268</head>
   269<body>
   270<div id="app">
   271  <header>
   272    <div class="logo"><img src="safecast-square-ct.png" alt="Safecast" /></div>
   273    <div class="title-area">
   274      <h1>Safecast Radiation Assistant</h1>
   275      <p>Radiation data from the Safecast sensor network</p>
   276    </div>
   277    <button id="download-btn" onclick="downloadConversation()">
   278      <svg viewBox="0 0 24 24"><path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/></svg>
   279      Download
   280    </button>
   281  </header>
   282
   283  <div id="messages">
   284    <div id="intro">
   285      <h2>Ask about radiation data</h2>
   286      <p>I have access to live readings from Safecast sensors across Japan and worldwide.</p>
   287      <div class="chips">
   288        <span class="chip" onclick="ask(this)">What's the radiation level near Tokyo?</span>
   289        <span class="chip" onclick="ask(this)">Show sensors near Fukushima</span>
   290        <span class="chip" onclick="ask(this)">What are the latest readings in Osaka?</span>
   291        <span class="chip" onclick="ask(this)">How does radiation compare year over year?</span>
   292      </div>
   293    </div>
   294  </div>
   295
   296  <div id="input-area">
   297    <form id="form" onsubmit="return false;">
   298      <textarea id="msg" rows="1" placeholder="Ask about radiation levels, sensors, or locations"></textarea>
   299      <button id="send" type="submit" title="Send">
   300        <svg width="18" height="18" viewBox="0 0 24 24"><path d="M2 21l21-9L2 3v7l15 2-15 2z"/></svg>
   301      </button>
   302    </form>
   303  </div>
   304</div>
   305
   306<script>
   307  const messagesEl = document.getElementById('messages');
   308  const introEl    = document.getElementById('intro');
   309  const formEl     = document.getElementById('form');
   310  const msgEl      = document.getElementById('msg');
   311  const sendBtn    = document.getElementById('send');
   312
   313  let busy = false;
   314  let conversationHistory = []; // Track conversation for context
   315
   316  // Auto-grow textarea
   317  msgEl.addEventListener('input', () => {
   318    msgEl.style.height = 'auto';
   319    msgEl.style.height = Math.min(msgEl.scrollHeight, 120) + 'px';
   320  });
   321
   322  // Send on Enter (Shift+Enter = newline)
   323  msgEl.addEventListener('keydown', e => {
   324    if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); submit(); }
   325  });
   326
   327  formEl.addEventListener('submit', submit);
   328
   329  function ask(chip) {
   330    msgEl.value = chip.textContent;
   331    submit();
   332  }
   333
   334  function submit() {
   335    const text = msgEl.value.trim();
   336    if (!text || busy) return;
   337    sendMessage(text);
   338    msgEl.value = '';
   339    msgEl.style.height = 'auto';
   340  }
   341
   342  // Convert markdown to HTML
   343  function markdownToHTML(text) {
   344    let html = text;
   345
   346    // Headers (must be at start of line)
   347    html = html.replace(/^### (.+)$/gm, '<h3>$1</h3>');
   348    html = html.replace(/^## (.+)$/gm, '<h2>$1</h2>');
   349    html = html.replace(/^# (.+)$/gm, '<h1>$1</h1>');
   350
   351    // Horizontal rules
   352    html = html.replace(/^---+$/gm, '<hr>');
   353
   354    // Tables (simple approach - detect table rows)
   355    html = html.replace(/^\|(.+)\|$/gm, function(match, content) {
   356      const cells = content.split('|').map(c => c.trim());
   357      const cellTags = cells.map(c => {
   358        // Check if this is a separator row (--|---|---)
   359        if (/^:?-+:?$/.test(c)) return null;
   360        return `<td>${c}</td>`;
   361      }).filter(Boolean);
   362      if (cellTags.length === 0) return '';
   363      return '<tr>' + cellTags.join('') + '</tr>';
   364    });
   365    // Wrap table rows in table tags
   366    html = html.replace(/(<tr>.+<\/tr>\n?)+/g, '<table>$&</table>');
   367
   368    // Unordered lists (lines starting with - or *)
   369    html = html.replace(/^[*-] (.+)$/gm, '<li>$1</li>');
   370    html = html.replace(/(<li>.+<\/li>\n?)+/g, '<ul>$&</ul>');
   371
   372    // Ordered lists (lines starting with 1., 2., etc)
   373    html = html.replace(/^\d+\. (.+)$/gm, '<li>$1</li>');
   374
   375    // Bold: **text** or __text__
   376    html = html.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
   377    html = html.replace(/__(.+?)__/g, '<strong>$1</strong>');
   378
   379    // Italic: *text* or _text_
   380    html = html.replace(/\*(.+?)\*/g, '<em>$1</em>');
   381    html = html.replace(/_(.+?)_/g, '<em>$1</em>');
   382
   383    // Inline code: `code`
   384    html = html.replace(/`(.+?)`/g, '<code>$1</code>');
   385
   386    // Links: [text](url)
   387    html = html.replace(/\[(.+?)\]\((.+?)\)/g, '<a href="$2" target="_blank" rel="noopener">$1</a>');
   388
   389    // Line breaks: preserve newlines
   390    html = html.replace(/\n/g, '<br>');
   391
   392    return html;
   393  }
   394
   395  function addMessage(role, text, cls) {
   396    if (introEl) introEl.style.display = 'none';
   397
   398    const msg    = document.createElement('div');
   399    msg.className = `msg ${role}`;
   400
   401    const avatar = document.createElement('div');
   402    avatar.className = 'avatar';
   403    avatar.textContent = role === 'user' ? '' : '';
   404
   405    const bubble = document.createElement('div');
   406    bubble.className = `bubble ${cls || ''}`;
   407    // User messages are plain text, bot messages support markdown
   408    if (role === 'bot') {
   409      bubble.innerHTML = markdownToHTML(text);
   410
   411      // Add copy button for bot messages
   412      const copyBtn = document.createElement('button');
   413      copyBtn.className = 'copy-btn';
   414      copyBtn.textContent = 'Copy';
   415      copyBtn.onclick = () => copyMessage(text, copyBtn);
   416      bubble.appendChild(copyBtn);
   417    } else {
   418      bubble.textContent = text;
   419    }
   420
   421    msg.appendChild(avatar);
   422    msg.appendChild(bubble);
   423    messagesEl.appendChild(msg);
   424    messagesEl.scrollTop = messagesEl.scrollHeight;
   425    return bubble;
   426  }
   427
   428  function copyMessage(text, btn) {
   429    navigator.clipboard.writeText(text).then(() => {
   430      const originalText = btn.textContent;
   431      btn.textContent = 'Copied!';
   432      btn.classList.add('copied');
   433      setTimeout(() => {
   434        btn.textContent = originalText;
   435        btn.classList.remove('copied');
   436      }, 2000);
   437    }).catch(err => {
   438      console.error('Failed to copy:', err);
   439    });
   440  }
   441
   442  function downloadConversation() {
   443    if (conversationHistory.length === 0) {
   444      alert('No conversation to download yet!');
   445      return;
   446    }
   447
   448    // Build markdown format
   449    let markdown = '# Safecast Radiation Assistant Conversation\n\n';
   450    markdown += `Date: ${new Date().toLocaleString()}\n\n`;
   451    markdown += '---\n\n';
   452
   453    conversationHistory.forEach(msg => {
   454      const role = msg.role === 'user' ? 'You' : 'Assistant';
   455      markdown += `## ${role}\n\n`;
   456      markdown += msg.content + '\n\n';
   457      markdown += '---\n\n';
   458    });
   459
   460    // Create download link
   461    const blob = new Blob([markdown], { type: 'text/markdown' });
   462    const url = URL.createObjectURL(blob);
   463    const a = document.createElement('a');
   464    a.href = url;
   465    a.download = `safecast-conversation-${new Date().toISOString().slice(0,10)}.md`;
   466    document.body.appendChild(a);
   467    a.click();
   468    document.body.removeChild(a);
   469    URL.revokeObjectURL(url);
   470  }
   471
   472  function sendMessage(text) {
   473    busy = true;
   474    sendBtn.disabled = true;
   475
   476    addMessage('user', text);
   477    const botBubble = addMessage('bot', 'thinking', 'thinking');
   478
   479    let accumulated = '';
   480
   481    function finish(success = true) {
   482      botBubble.classList.remove('thinking');
   483      busy = false;
   484      sendBtn.disabled = false;
   485      msgEl.focus();
   486
   487      // Update conversation history on successful completion
   488      if (success && accumulated) {
   489        conversationHistory.push(
   490          { role: 'user', content: text },
   491          { role: 'assistant', content: accumulated }
   492        );
   493
   494        // Keep only last 10 messages (5 exchanges) to prevent rate limits
   495        const MAX_HISTORY = 10;
   496        if (conversationHistory.length > MAX_HISTORY) {
   497          conversationHistory = conversationHistory.slice(-MAX_HISTORY);
   498        }
   499      }
   500    }
   501
   502    fetch('/chat', {
   503      method: 'POST',
   504      headers: { 'Content-Type': 'application/json' },
   505      body: JSON.stringify({
   506        message: text,
   507        history: conversationHistory
   508      }),
   509    }).then(response => {
   510      const reader  = response.body.getReader();
   511      const decoder = new TextDecoder();
   512      let   buffer  = '';
   513
   514      function pump() {
   515        return reader.read().then(({ done, value }) => {
   516          if (done) { finish(); return; }
   517
   518          buffer += decoder.decode(value, { stream: true });
   519          const lines = buffer.split('\n');
   520          buffer = lines.pop(); // keep incomplete last line
   521
   522          for (const line of lines) {
   523            if (!line.trim()) continue;
   524            try {
   525              const ev = JSON.parse(line);
   526              if (ev.type === 'text') {
   527                if (botBubble.classList.contains('thinking')) {
   528                  botBubble.classList.remove('thinking');
   529                  accumulated = '';
   530                }
   531                accumulated += ev.text;
   532                botBubble.innerHTML = markdownToHTML(accumulated);
   533                messagesEl.scrollTop = messagesEl.scrollHeight;
   534              } else if (ev.type === 'done') {
   535                finish();
   536              } else if (ev.type === 'error') {
   537                botBubble.classList.remove('thinking');
   538                botBubble.classList.add('error');
   539                botBubble.textContent = 'Error: ' + ev.error;
   540                finish(false);
   541              }
   542            } catch { /* malformed line, skip */ }
   543          }
   544          return pump();
   545        });
   546      }
   547      return pump();
   548    }).catch(err => {
   549      botBubble.classList.remove('thinking');
   550      botBubble.classList.add('error');
   551      botBubble.textContent = 'Connection error: ' + err.message;
   552      finish(false);
   553    });
   554  }
   555
   556  msgEl.focus();
   557</script>
   558</body>
   559</html>
