package main

import (
	"context"
	"fmt"

	"github.com/mark3labs/mcp-go/mcp"
)

var listSensorsToolDef = mcp.NewTool("list_sensors",
	mcp.WithDescription("Discover active fixed sensors (Pointcast, Solarcast, bGeigieZen, etc.) by location or type, returning device IDs, locations, status, and last reading timestamp. IMPORTANT: Every response includes an _ai_generated_note field. You MUST display this note verbatim to the user in every response that uses data from this tool. CRITICAL: Present all findings in an objective, scientific manner without using personal pronouns (I, we, I'll, you) or conversational language (Perfect!, Great!). Format as factual statements only."),
	mcp.WithString("type",
		mcp.Description("Filter by sensor type (e.g., 'Pointcast', 'Solarcast', 'bGeigieZen', etc.)"),
	),
	mcp.WithNumber("min_lat",
		mcp.Description("Southern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("max_lat",
		mcp.Description("Northern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("min_lon",
		mcp.Description("Western boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("max_lon",
		mcp.Description("Eastern boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("limit",
		mcp.Description("Maximum number of sensors to return (default: 50, max: 1000)"),
		mcp.Min(1), mcp.Max(1000),
		mcp.DefaultNumber(50),
	),
	mcp.WithReadOnlyHintAnnotation(true),
)

func handleListSensors(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
	sensorType := req.GetString("type", "")
	minLat := req.GetFloat("min_lat", -90)
	maxLat := req.GetFloat("max_lat", 90)
	minLon := req.GetFloat("min_lon", -180)
	maxLon := req.GetFloat("max_lon", 180)
	limit := req.GetInt("limit", 50)

	if limit < 1 || limit > 1000 {
		return mcp.NewToolResultError("Limit must be between 1 and 1000"), nil
	}

	if dbAvailable() {
		return listSensorsDB(ctx, sensorType, minLat, maxLat, minLon, maxLon, limit)
	}

	// Fallback to API if database not available
	return mcp.NewToolResultError("Database connection required for list_sensors tool. Please ensure DATABASE_URL is set to access real-time sensor data."), nil
}

func listSensorsDB(ctx context.Context, sensorType string, minLat, maxLat, minLon, maxLon float64, limit int) (*mcp.CallToolResult, error) {
	// Check what tables are available in the database
	tablesQuery := `
		SELECT table_name 
		FROM information_schema.tables 
		WHERE table_schema = 'public'
		ORDER BY table_name
	`

	tableRows, err := queryRows(ctx, tablesQuery)
	if err != nil {
		return mcp.NewToolResultError("Could not query database schema: " + err.Error()), nil
	}

	// Look for tables that might contain real-time sensor data
	availableTables := make([]string, len(tableRows))
	realtimeTable := ""
	for i, row := range tableRows {
		if tableName, ok := row["table_name"].(string); ok {
			availableTables[i] = tableName
			// Check for possible real-time sensor data tables
			if tableName == "realtime_measurements" || 
			   tableName == "measurements_realtime" || 
			   tableName == "sensors" ||
			   tableName == "devices" {
				realtimeTable = tableName
			}
		}
	}

	if realtimeTable == "" {
		// If no real-time table found, return available tables for debugging
		result := map[string]any{
			"message": "No known real-time sensor data tables found in database.",
			"available_tables": availableTables,
			"suggestion": "Real-time sensor data may not be available through this database connection.",
		}
		return jsonResult(result)
	}

	// Query the appropriate real-time table to find unique devices/sensors
	var query string
	var args []interface{}

	if sensorType != "" {
		// Filter by sensor type
		// FIXED: Get the actual latest reading per device, not grouped by lat/lon
		// which causes stale data when sensors move or have multiple positions
		query = fmt.Sprintf(`
			SELECT
				rm.device_id,
				COALESCE(rm.device_name, rm.device_id) AS device_name,
				COALESCE(rm.transport, '') AS transport,
				rm.lat AS latitude,
				rm.lon AS longitude,
				to_timestamp(rm.measured_at) AS last_reading_at
			FROM %s rm
			INNER JOIN (
				SELECT device_id, MAX(measured_at) as max_measured_at
				FROM %s
				WHERE lat >= $1 AND lat <= $2 AND lon >= $3 AND lon <= $4
					AND (COALESCE(transport, '') ILIKE $5 OR COALESCE(device_name, '') ILIKE $5)
				GROUP BY device_id
			) latest ON rm.device_id = latest.device_id AND rm.measured_at = latest.max_measured_at
			WHERE rm.lat >= $1 AND rm.lat <= $2 AND rm.lon >= $3 AND rm.lon <= $4
			ORDER BY rm.measured_at DESC
			LIMIT $6`, realtimeTable, realtimeTable)

		args = []interface{}{minLat, maxLat, minLon, maxLon, "%" + sensorType + "%", limit}
	} else {
		// No filter by type
		// FIXED: Get the actual latest reading per device, not grouped by lat/lon
		query = fmt.Sprintf(`
			SELECT
				rm.device_id,
				COALESCE(rm.device_name, rm.device_id) AS device_name,
				COALESCE(rm.transport, '') AS transport,
				rm.lat AS latitude,
				rm.lon AS longitude,
				to_timestamp(rm.measured_at) AS last_reading_at
			FROM %s rm
			INNER JOIN (
				SELECT device_id, MAX(measured_at) as max_measured_at
				FROM %s
				WHERE lat >= $1 AND lat <= $2 AND lon >= $3 AND lon <= $4
				GROUP BY device_id
			) latest ON rm.device_id = latest.device_id AND rm.measured_at = latest.max_measured_at
			WHERE rm.lat >= $1 AND rm.lat <= $2 AND rm.lon >= $3 AND rm.lon <= $4
			ORDER BY rm.measured_at DESC
			LIMIT $5`, realtimeTable, realtimeTable)

		args = []interface{}{minLat, maxLat, minLon, maxLon, limit}
	}

	rows, err := queryRows(ctx, query, args...)
	if err != nil {
		return mcp.NewToolResultError(fmt.Sprintf("Error querying %s table: %v", realtimeTable, err)), nil
	}

	sensors := make([]map[string]any, len(rows))
	for i, r := range rows {
		sensors[i] = map[string]any{
			"device_id":       r["device_id"],
			"device_name":     r["device_name"],
			"type":            r["transport"],
			"location": map[string]any{
				"latitude":  r["latitude"],
				"longitude": r["longitude"],
			},
			"last_reading_at": r["last_reading_at"],
		}
	}

	result := map[string]any{
		"count":   len(sensors),
		"source":  "database",
		"sensors": sensors,
		"table_used": realtimeTable,
		"available_tables": availableTables,
		"_ai_hint": "CRITICAL INSTRUCTIONS: (1) The 'unit' field indicates measurement units - CPM means 'counts per minute' NOT 'counts per second'. Always interpret and report CPM values as counts per minute. (2) Present all data in a purely scientific, factual manner. NEVER use personal pronouns (I, we, I'll, I'm, you, your), exclamations (!, Perfect, Great, Excellent), or conversational phrases. Format responses as objective statements: 'Latest reading: X CPM at location Y' NOT 'I found a reading of X CPM' or 'Perfect! The sensor shows...'. State only objective facts and measurements.",
		"_ai_generated_note": "This data was retrieved by an AI assistant using Safecast tools. The interpretation and presentation of this data may be influenced by the AI system.",
	}

	return jsonResult(result)
}

package main

import (
	"context"
	"fmt"
	"strings"

	"github.com/mark3labs/mcp-go/mcp"
)

var sensorCurrentToolDef = mcp.NewTool("sensor_current",
	mcp.WithDescription("Get the MOST RECENT readings from REAL-TIME fixed sensors (Pointcast, Solarcast, bGeigieZen, etc.). **USE THIS TOOL** when users ask about 'current', 'latest', 'live', 'now', or 'real-time' sensor data. This returns the freshest data available from the realtime_measurements table. **DO NOT** use query_radiation for current sensor data (that tool searches historical markers, not real-time sensors). For mobile bGeigie devices, use device_history. For historical trends from sensors, use sensor_history. Always check and report the captured_at timestamp to users. CPM = counts per minute (convert to Sv/h using ~0.0069 factor for LND 7318 detectors). Present data objectively without personal pronouns or conversational language."),
	mcp.WithString("device_id",
		mcp.Description("Specific device ID to get latest reading from"),
	),
	mcp.WithNumber("min_lat",
		mcp.Description("Southern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("max_lat",
		mcp.Description("Northern boundary for geographic filter"),
		mcp.Min(-90), mcp.Max(90),
	),
	mcp.WithNumber("min_lon",
		mcp.Description("Western boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("max_lon",
		mcp.Description("Eastern boundary for geographic filter"),
		mcp.Min(-180), mcp.Max(180),
	),
	mcp.WithNumber("limit",
		mcp.Description("Maximum number of readings to return (default: 25, max: 1000)"),
		mcp.Min(1), mcp.Max(1000),
		mcp.DefaultNumber(25),
	),
	mcp.WithReadOnlyHintAnnotation(true),
)

func handleSensorCurrent(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
	deviceID := req.GetString("device_id", "")
	minLat := req.GetFloat("min_lat", -90)
	maxLat := req.GetFloat("max_lat", 90)
	minLon := req.GetFloat("min_lon", -180)
	maxLon := req.GetFloat("max_lon", 180)
	limit := req.GetInt("limit", 25)

	if limit < 1 || limit > 1000 {
		return mcp.NewToolResultError("Limit must be between 1 and 1000"), nil
	}

	if dbAvailable() {
		return sensorCurrentDB(ctx, deviceID, minLat, maxLat, minLon, maxLon, limit)
	}

	// Fallback to API if database not available
	return mcp.NewToolResultError("Database connection required for sensor_current tool. Please ensure DATABASE_URL is set to access real-time sensor data."), nil
}

func sensorCurrentDB(ctx context.Context, deviceID string, minLat, maxLat, minLon, maxLon float64, limit int) (*mcp.CallToolResult, error) {
	// Check what tables are available in the database
	tablesQuery := `
		SELECT table_name 
		FROM information_schema.tables 
		WHERE table_schema = 'public'
		ORDER BY table_name
	`

	tableRows, err := queryRows(ctx, tablesQuery)
	if err != nil {
		return mcp.NewToolResultError("Could not query database schema: " + err.Error()), nil
	}

	// Look for tables that might contain real-time sensor data
	availableTables := make([]string, len(tableRows))
	realtimeTable := ""
	for i, row := range tableRows {
		if tableName, ok := row["table_name"].(string); ok {
			availableTables[i] = tableName
			// Check for possible real-time sensor data tables
			if tableName == "realtime_measurements" || 
			   tableName == "measurements_realtime" || 
			   tableName == "sensors" ||
			   tableName == "devices" {
				realtimeTable = tableName
			}
		}
	}

	if realtimeTable == "" {
		// If no real-time table found, return available tables for debugging
		result := map[string]any{
			"message": "No known real-time sensor data tables found in database.",
			"available_tables": availableTables,
			"suggestion": "Real-time sensor data may not be available through this database connection.",
		}
		return jsonResult(result)
	}

	var query string
	var args []interface{}

	if deviceID != "" {
		// Get latest reading from specific device
		query = fmt.Sprintf(`
			SELECT
				id,
				device_id,
				COALESCE(device_name, device_id) AS device_name,
				value,
				COALESCE(unit, 'Sv/h') AS unit,
				to_timestamp(measured_at) AS captured_at,
				lat AS latitude,
				lon AS longitude,
				COALESCE(transport, '') AS transport
			FROM %s
			WHERE device_id = $1
			  AND to_timestamp(measured_at) <= NOW()
			ORDER BY measured_at DESC
			LIMIT 1`, realtimeTable)

		args = []interface{}{deviceID}
	} else {
		// Get latest readings from all sensors in geographic area
		query = fmt.Sprintf(`
			SELECT
				rm.id,
				rm.device_id,
				COALESCE(rm.device_name, rm.device_id) AS device_name,
				rm.value,
				COALESCE(rm.unit, 'Sv/h') AS unit,
				to_timestamp(rm.measured_at) AS captured_at,
				rm.lat AS latitude,
				rm.lon AS longitude,
				COALESCE(rm.transport, '') AS transport
			FROM %s rm
			INNER JOIN (
				SELECT device_id, MAX(measured_at) as max_measured_at
				FROM %s
				WHERE lat >= $1 AND lat <= $2 AND lon >= $3 AND lon <= $4
				  AND to_timestamp(measured_at) <= NOW()
				GROUP BY device_id
			) latest ON rm.device_id = latest.device_id AND rm.measured_at = latest.max_measured_at
			WHERE rm.lat >= $1 AND rm.lat <= $2 AND rm.lon >= $3 AND rm.lon <= $4
			  AND to_timestamp(rm.measured_at) <= NOW()
			ORDER BY rm.measured_at DESC
			LIMIT $5`, realtimeTable, realtimeTable)

		args = []interface{}{minLat, maxLat, minLon, maxLon, limit}
	}

	rows, err := queryRows(ctx, query, args...)
	if err != nil {
		return mcp.NewToolResultError(fmt.Sprintf("Error querying %s table: %v", realtimeTable, err)), nil
	}

	readings := make([]map[string]any, len(rows))
	for i, r := range rows {
		// Fix incorrect unit: Geiger counters report in CPM (counts per minute), not CPS
		unit := r["unit"]
		if unitStr, ok := unit.(string); ok {
			unit = strings.ReplaceAll(strings.ReplaceAll(unitStr, "cps", "cpm"), "CPS", "CPM")
		}

		readings[i] = map[string]any{
			"id":          r["id"],
			"device_id":   r["device_id"],
			"device_name": r["device_name"],
			"value":       r["value"],
			"unit":        unit,
			"captured_at": r["captured_at"],
			"location": map[string]any{
				"latitude":  r["latitude"],
				"longitude": r["longitude"],
			},
			"type": r["transport"],
		}
	}

	result := map[string]any{
		"count":    len(readings),
		"source":   "database",
		"readings": readings,
		"table_used": realtimeTable,
		"available_tables": availableTables,
		"_ai_hint": "CRITICAL INSTRUCTIONS: (1) **REAL-TIME DATA**: This tool returns the MOST RECENT readings from fixed sensors. Readings with future timestamps (sensor clock errors) are automatically filtered out. Always check the 'captured_at' timestamp and report it to the user - if the data is more than 24 hours old, mention this to the user and suggest checking if the sensor is still active. (2) **UNITS**: CPM means 'counts per minute' NOT 'counts per second'. Always convert to Sv/h using detector-specific factors (LND 7318: ~0.0069 Sv/h per CPM). (3) **TOOL SELECTION**: For latest sensor data, use 'sensor_current'. For historical trends, use 'sensor_history'. For mobile measurements, use 'device_history'. Do NOT use 'query_radiation' for current sensor data as it searches the historical markers table. (4) **PRESENTATION**: State objective facts only - no personal pronouns (I, we, you), exclamations, or conversational phrases. Example: 'Device X reading: 50 CPM (0.35 Sv/h) at [time]' NOT 'I found a reading...'",
		"_ai_generated_note": "This data was retrieved by an AI assistant using Safecast tools. The interpretation and presentation of this data may be influenced by the AI system.",
	}

	return jsonResult(result)
}

package main

import (
	"bytes"
	"context"
	_ "embed"
	"encoding/json"
	"fmt"
	"io"
	"log"
	"net/http"
	"os"

	mcpclient "github.com/mark3labs/mcp-go/client"
	"github.com/mark3labs/mcp-go/mcp"
)

//go:embed index.html
var indexHTML []byte

//go:embed safecast-square-ct.png
var logoPNG []byte

const systemPrompt = `You are a helpful assistant for the Safecast radiation monitoring network.
You have access to both REAL-TIME sensor data and historical measurement archives.

**CRITICAL: Tool Selection**
